import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import AuxiliaryConverter from "../components/AuxiliaryConverter";

/* --- category slug lists --- */
const mainCategories = ["length", "temperature", "area", "volume", "mass", "time"];

const allCategories = [
  "acceleration", "angular-acceleration", "angular-velocity", "area", "capacitance",
  "charge", "current", "density", "electric-field", "energy", "force", "frequency",
  "illuminance", "length", "magnetic-flux-density", "mass", "moment-of-inertia",
  "power", "pressure", "resistance", "resistivity", "specific-heat", "speed",
  "surface-tension", "thermal-conductivity", "temperature", "time", "torque",
  "viscosity", "volume", "voltage", "youngs-modulus"
];

/* util: slug -> Pretty Title */
const toTitleCase = (slug) =>
  slug.split("-").map(w => w.charAt(0).toUpperCase() + w.slice(1)).join(" ");

export default function Home() {
  const [activeMain, setActiveMain] = useState(mainCategories[0]);
  const navigate = useNavigate();

  return (
    <>
      {/* ---------- Header ------------ */}
      <header className="w-full bg-white shadow-sm flex items-center gap-3 px-4 py-3">
        <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8 text-blue-600" fill="none"
             viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}>
          <path strokeLinecap="round" strokeLinejoin="round"
                d="M12 8c-1.657 0-3 1.343-3 3v6h6v-6c0-1.657-1.343-3-3-3z" />
          <path strokeLinecap="round" strokeLinejoin="round"
                d="M12 3v2m0 14v2m7-7h2M3 12h2" />
        </svg>
        <h1 className="text-2xl font-semibold text-blue-700 select-none">Unit Converter</h1>
      </header>

      {/* ---------- Main (grey background) ------------ */}
      <main className="flex-grow bg-gray-100 p-6 min-h-screen">
        {/* Section 1 – Main‑tabs + AuxiliaryConverter */}
        <section className="max-w-4xl mx-auto mb-12">
          <div className="flex flex-wrap justify-center gap-3 mb-6">
            {mainCategories.map(slug => (
              <button
                key={slug}
                onClick={() => setActiveMain(slug)}
                className={`px-4 py-2 rounded font-semibold border
                  ${activeMain === slug
                    ? "bg-blue-600 text-white border-blue-600"
                    : "bg-white text-gray-700 border-gray-300 hover:bg-blue-50"}`}
              >
                {toTitleCase(slug)}
              </button>
            ))}
          </div>

          <div className="bg-white p-6 rounded shadow">
            <AuxiliaryConverter categoryName={activeMain} />
          </div>
        </section>

        {/* Section 2 – All Converters grid */}
        <section className="max-w-6xl mx-auto">
          <h2 className="text-xl font-semibold text-center mb-6">All Converters</h2>
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4">
            {allCategories.map(slug => (
              <button
                key={slug}
                onClick={() => navigate(`/category/${slug}`)}
                className="bg-white shadow hover:bg-blue-50 text-gray-800 font-medium
                           border border-gray-200 rounded px-4 py-3 text-center"
              >
                {toTitleCase(slug)}
              </button>
            ))}
          </div>
        </section>
      </main>

      {/* ---------- Footer ------------ */}
      <footer className="w-full bg-white border-t text-gray-600 text-center text-sm py-4 select-none">
        © 2025 Unit Converter App • All rights reserved
      </footer>
    </>
  );
}
