import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { useTheme } from "../context/ThemeContext";
import { formatNumber } from "../utils/formatNumber"; // ✅ Added import

function parseScientific(val) {
  if (val == null) return null;
  if (typeof val === "number") return val;
  if (typeof val === "object" && val.value) return parseFloat(val.value);
  return parseFloat(val);
}

const Converter = ({ categoryId }) => {
  const theme = useTheme();
  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [inputValue, setInputValue] = useState(1);
  const [inputUnit, setInputUnit] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const unitData = await pb.collection("units").getFullList({ filter: `category="${categoryId}"` });
        const itemData = await pb.collection("realworld_items").getFullList({ filter: `category="${categoryId}"` });

        const sortedItems = itemData.sort((a, b) => {
          const aVal = parseScientific(a.value);
          const bVal = parseScientific(b.value);
          return aVal - bVal;
        });

        setUnits(unitData);
        setRealWorldItems(sortedItems);

        if (unitData.length > 0) {
          setInputUnit(unitData[0]);
        }
      } catch (error) {
        console.error("Error loading data:", error);
      }
    };

    fetchData();
  }, [categoryId]);

  const convertValue = (value, fromUnit, toUnit) => {
    if (!fromUnit || !toUnit) return null;
    return (value * fromUnit.to_base_factor) / toUnit.to_base_factor;
  };

  const handleInputChange = (e) => {
    const val = parseFloat(e.target.value);
    if (!isNaN(val)) setInputValue(val);
  };

  return (
    <div className="p-4">
      <div className="mb-4">
        <input
          type="number"
          value={inputValue}
          onChange={handleInputChange}
          className="border p-2 rounded"
        />
        <select
          value={inputUnit?.id || ""}
          onChange={(e) =>
            setInputUnit(units.find((u) => u.id === e.target.value))
          }
          className="ml-2 p-2 border rounded"
        >
          {units.map((unit) => (
            <option key={unit.id} value={unit.id}>
              {unit.name}
            </option>
          ))}
        </select>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {units.slice(0, 3).map((targetUnit, index) => {
          const result = convertValue(inputValue, inputUnit, targetUnit);
          return (
            <div
              key={index}
              className="p-4 rounded shadow"
              style={{ backgroundColor: theme?.box || "#fff" }}
            >
              <div className="font-bold text-lg mb-2">
                {formatNumber(result)} {targetUnit.symbol}
              </div>
              <div className="text-sm text-gray-500">{targetUnit.name}</div>
            </div>
          );
        })}
      </div>

      <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-4">
        {realWorldItems.slice(0, 3).map((item, idx) => {
          const itemValue = parseScientific(item.value);
          const ratio = convertValue(inputValue, inputUnit, { to_base_factor: itemValue });

          return (
            <RealWorldBox
              key={idx}
              item={item}
              ratio={ratio}
              theme={theme}
            />
          );
        })}
      </div>
    </div>
  );
};

export default Converter;
