import React from "react";
import { formatNumber } from "../utils/formatNumber";

const ComparisonBox = ({
  index,
  itemId,
  realWorldItems,
  comparisonToggles,
  setComparisonToggles,
  selectedItems,
  setSelectedItems,
  getComparisonValue,
  theme,
}) => {
  const currentItem = realWorldItems.find((i) => i.id === itemId);

  return (
    <div className="p-4 rounded shadow flex flex-col gap-3" style={{ backgroundColor: theme?.box }}>
      <div className="flex justify-center gap-2">
        <button
          className={`px-3 py-1 rounded-l ${
            !comparisonToggles[index] ? "bg-[#FEB73F] text-white" : "bg-white border text-black"
          }`}
          style={{ borderColor: "#ccc" }}
          onClick={() => setComparisonToggles((prev) => prev.map((t, i) => (i === index ? false : t)))}
        >
          General
        </button>
        <button
          className={`px-3 py-1 rounded-r ${
            comparisonToggles[index] ? "bg-[#FEB73F] text-white" : "bg-white border text-black"
          }`}
          style={{ borderColor: "#ccc" }}
          onClick={() => setComparisonToggles((prev) => prev.map((t, i) => (i === index ? true : t)))}
        >
          Scientific
        </button>
      </div>

      <div className="bg-gray-100 p-3 rounded text-center text-blue-700 font-bold text-base min-h-[48px]">
        {formatNumber(getComparisonValue(itemId), comparisonToggles[index])}
      </div>

      <div className="border rounded max-h-36 overflow-y-auto text-sm bg-white">
        {realWorldItems.map((item) => (
          <div
            key={item.id}
            className={`cursor-pointer p-1 hover:bg-blue-100 ${
              itemId === item.id ? "bg-blue-200 font-medium" : ""
            }`}
            onClick={() => setSelectedItems((prev) => prev.map((id, i) => (i === index ? item.id : id)))}
          >
            <div className="font-medium">{item.name}</div>
            <div className="text-xs text-gray-600">
              General: {formatNumber(item.approx_value, false)} <br />
              Scientific: {formatNumber(item.scientific_value, true)}
            </div>
            {item.notes && <div className="text-[10px] text-gray-500 italic">{item.notes}</div>}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ComparisonBox;
