import React, { useState, useEffect } from "react";
import PocketBase from "pocketbase";
import "./UnitConverter.css";

const pb = new PocketBase("http://127.0.0.1:8090");
pb.autoCancellation(false);

const formatNumber = (value, scientific) => {
  if (value === null || value === undefined || isNaN(value)) return "...";
  const abs = Math.abs(value);
  if (scientific || (abs !== 0 && (abs >= 1e6 || abs < 1e-3))) {
    const [base, expRaw] = value.toExponential(2).split("e");
    const exp = expRaw.replace("+", "");
    return (
      <>
        {parseFloat(base).toFixed(2)}×10<sup>{exp}</sup>
      </>
    );
  }
  return parseFloat(value).toLocaleString(undefined, {
    minimumFractionDigits: 2,
    maximumFractionDigits: 6,
  });
};

function YoungsModuleConverter({ selectedCategory }) {
  const [units, setUnits] = useState([]);
  const [selectedFromUnit, setSelectedFromUnit] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [toUnit1, setToUnit1] = useState("");
  const [toUnit2, setToUnit2] = useState("");
  const [toUnit3, setToUnit3] = useState("");
  const [sciConv1, setSciConv1] = useState(false);
  const [sciConv2, setSciConv2] = useState(false);
  const [sciConv3, setSciConv3] = useState(false);

  useEffect(() => {
    const fetchCategory = async () => {
      const categories = await pb.collection("categories").getFullList();
      const match = categories.find((c) => c.name === selectedCategory);
      if (match) setCategoryId(match.id);
    };
    if (selectedCategory) fetchCategory();
  }, [selectedCategory]);

  useEffect(() => {
    if (categoryId) {
      pb.collection("units")
        .getFullList({ filter: `category = "${categoryId}"` })
        .then((units) => {
          const sorted = [...units].sort((a, b) => a.to_base_factor - b.to_base_factor);
          setUnits(sorted);
          if (!selectedFromUnit) setSelectedFromUnit(sorted[0]?.id || "");
          if (!toUnit1) setToUnit1(sorted[1]?.id || "");
          if (!toUnit2) setToUnit2(sorted[2]?.id || "");
          if (!toUnit3) setToUnit3(sorted[3]?.id || "");
        })
        .catch(console.error);
    }
  }, [categoryId]);

  const getConverted = (toUnitId, scientific) => {
    if (!inputValue || !selectedFromUnit || !toUnitId) return "";
    const from = units.find((u) => u.id === selectedFromUnit);
    const to = units.find((u) => u.id === toUnitId);
    if (!from || !to) return "";
    const result = (parseFloat(inputValue) * from.to_base_factor) / to.to_base_factor;
    return formatNumber(result, scientific);
  };

  if (!selectedCategory) return null;

  return (
    <div className="max-w-6xl mx-auto px-4 py-6 bg-white rounded-lg shadow">
      <div className="flex flex-col md:flex-row gap-6 items-stretch">
        <div className="md:w-1/4 flex flex-col justify-center">
          <div className="space-y-4">
            <h3 className="text-lg font-semibold text-center">From</h3>
            <input
              type="number"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              className="border p-2 rounded w-full"
              placeholder="Enter value"
            />
            <select
              value={selectedFromUnit}
              onChange={(e) => setSelectedFromUnit(e.target.value)}
              className="border p-2 rounded w-full"
            >
              {units.map((u) => (
                <option key={u.id} value={u.id}>
                  {u.name} ({u.symbol})
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="md:w-3/4 space-y-10">
          <div>
            <h2 className="text-2xl font-bold mb-4 text-center">Conversion</h2>
            <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
              {[toUnit1, toUnit2, toUnit3].map((id, index) => {
                const setId = index === 0 ? setToUnit1 : index === 1 ? setToUnit2 : setToUnit3;
                const sciState = index === 0 ? sciConv1 : index === 1 ? sciConv2 : sciConv3;
                const setSci = index === 0 ? setSciConv1 : index === 1 ? setSciConv2 : setSciConv3;
                return (
                  <div key={index} className="flex flex-col gap-2">
                    <div className="flex justify-center">
                      <button
                        className={`px-3 py-1 rounded-l ${!sciState ? "bg-blue-600 text-white" : "bg-white border"}`}
                        onClick={() => setSci(false)}
                      >
                        General
                      </button>
                      <button
                        className={`px-3 py-1 rounded-r ${sciState ? "bg-blue-600 text-white" : "bg-white border"}`}
                        onClick={() => setSci(true)}
                      >
                        Scientific
                      </button>
                    </div>
                    <div className="border rounded p-4 bg-white shadow-sm">
                      <div className="text-xl font-bold text-blue-600 mb-2 text-center break-words">
                        {getConverted(id, sciState) || "..."}
                      </div>
                      <select
                        value={id}
                        onChange={(e) => setId(e.target.value)}
                        className="border p-2 rounded w-full text-sm text-center"
                      >
                        <option value="">-- Convert To --</option>
                        {units.map((u) => (
                          <option key={u.id} value={u.id}>
                            {u.name} ({u.symbol}) – {getConverted(u.id, sciState)}
                          </option>
                        ))}
                      </select>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default YoungsModuleConverter;
