import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { formatNumber } from "../utils/formatNumber";

// Helper to parse scientific_value from raw/mantissa/exponent formats
function parseScientific(val) {
  if (val == null) return null;
  if (typeof val === "number") return val;
  if (typeof val === "object" && "mantissa" in val && "exponent" in val) {
    return val.mantissa * Math.pow(10, val.exponent);
  }
  const num = Number(val);
  return isNaN(num) ? null : num;
}

function Converter({ categoryId }) {
  const [units, setUnits] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [fromUnit, setFromUnit] = useState("");
  const [selectedUnits, setSelectedUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [selectedItems, setSelectedItems] = useState([null, null, null]);
  const [comparisonToggles, setComparisonToggles] = useState([false, false, false]);
  const [conversionToggles, setConversionToggles] = useState([false, false, false]);
  const [categoryInfo, setCategoryInfo] = useState(null);
  const [prefixes, setPrefixes] = useState([]); // ✅ New

  useEffect(() => {
    const fetchData = async () => {
      try {
        const unitList = await pb.collection("units").getFullList({
          filter: `category = "${categoryId}"`,
        });
        const sorted = unitList.sort((a, b) => a.to_base_factor - b.to_base_factor);
        setUnits(sorted);
        setFromUnit(sorted[0]?.id || "");
        setSelectedUnits(sorted.slice(1, 4).map((u) => u.id));

        const realItems = await pb.collection("realworld_items").getFullList({
          filter: `category = "${categoryId}"`,
          expand: "unit",
        });

        const sortedItems = realItems.sort((a, b) => {
          const isAInfinite = a.expression?.includes("infin");
          const isBInfinite = b.expression?.includes("infin");

          if (isAInfinite && !isBInfinite) return 1;
          if (!isAInfinite && isBInfinite) return -1;
          if (isAInfinite && isBInfinite) return 0;

          const aVal = parseScientific(a.scientific_value);
          const bVal = parseScientific(b.scientific_value);

          return aVal - bVal;
        });

        setRealWorldItems(sortedItems);
        setSelectedItems([
          sortedItems[0] || null,
          sortedItems[1] || null,
          sortedItems[2] || null,
        ]);

        const category = await pb.collection("categories").getOne(categoryId);
        setCategoryInfo(category);

        const prefixList = await pb.collection("prefixes").getFullList(); // ✅ Fetch
        setPrefixes(prefixList);
      } catch (err) {
        console.error("Fetch error:", err);
      }
    };

    if (categoryId) fetchData();
  }, [categoryId]);

  const getConvertedValue = (toUnitId) => {
    const from = units.find((u) => u.id === fromUnit);
    const to = units.find((u) => u.id === toUnitId);
    if (!from || !to || !inputValue) return null;
    const baseValue = parseFloat(inputValue) * from.to_base_factor;
    return baseValue / to.to_base_factor;
  };

  const getComparisonValue = (item) => {
    const from = units.find((u) => u.id === fromUnit);
    const approx = item?.approx_value;
    if (!from || !approx || !inputValue) return null;
    const baseValue = parseFloat(inputValue) * from.to_base_factor;
    return baseValue / approx;
  };

  return (
    <div className="space-y-10">
      {/* ✅ Top Notes */}
      {categoryInfo?.top_notes && (
        <div
          className="bg-yellow-100 border-l-4 border-yellow-500 p-4 rounded text-sm text-gray-800 mb-6"
          dangerouslySetInnerHTML={{ __html: categoryInfo.top_notes }}
        />
      )}

      <div className="flex flex-col lg:flex-row gap-6">
        {/* From Section */}
        <div className="w-full lg:w-64 flex flex-col items-center justify-center gap-4">
          <label className="font-semibold text-lg">From</label>
          <input
            type="number"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            placeholder="Enter value"
            className="border p-2 rounded w-full"
          />
          <div className="border rounded max-h-40 overflow-y-auto w-full text-sm space-y-1">
            {units.map((u) => (
              <div
                key={u.id}
                className={`cursor-pointer p-1 rounded hover:bg-blue-100 ${
                  fromUnit === u.id ? "bg-blue-200 font-medium" : ""
                }`}
                onClick={() => setFromUnit(u.id)}
              >
                {u.name} ({u.symbol})
              </div>
            ))}
          </div>
        </div>

        {/* Main Right Section */}
        <div className="flex-1 space-y-10">
          {/* Conversion */}
          <div>
            <div className="text-center text-xl font-bold text-gray-700 mb-2">Conversion</div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {selectedUnits.map((toUnitId, index) => {
                const currentUnit = units.find((u) => u.id === toUnitId);
                return (
                  <div key={index} className="bg-white p-4 rounded shadow flex flex-col gap-3">
                    <div className="flex justify-center gap-2">
                      <button
                        className={`px-3 py-1 rounded-l ${
                          !conversionToggles[index] ? "bg-blue-600 text-white" : "bg-white border"
                        }`}
                        onClick={() =>
                          setConversionToggles((prev) =>
                            prev.map((t, i) => (i === index ? false : t))
                          )
                        }
                      >
                        General
                      </button>
                      <button
                        className={`px-3 py-1 rounded-r ${
                          conversionToggles[index] ? "bg-blue-600 text-white" : "bg-white border"
                        }`}
                        onClick={() =>
                          setConversionToggles((prev) =>
                            prev.map((t, i) => (i === index ? true : t))
                          )
                        }
                      >
                        Scientific
                      </button>
                    </div>
                    <div className="bg-gray-100 p-3 rounded text-center text-blue-700 font-bold text-base min-h-[48px]">
                      {formatNumber(getConvertedValue(toUnitId), conversionToggles[index])}{" "}
                      {currentUnit?.symbol || ""}
                    </div>
                    <div className="border rounded max-h-36 overflow-y-auto text-sm">
                      {units.map((u) => (
                        <div
                          key={u.id}
                          className={`cursor-pointer p-1 hover:bg-blue-100 ${
                            toUnitId === u.id ? "bg-blue-200 font-medium" : ""
                          }`}
                          onClick={() =>
                            setSelectedUnits((prev) =>
                              prev.map((id, i) => (i === index ? u.id : id))
                            )
                          }
                        >
                          {u.name} ({u.symbol}) -{" "}
                          {formatNumber(getConvertedValue(u.id), conversionToggles[index])}
                        </div>
                      ))}
                    </div>
                  </div>
                );
              })}
            </div>
          </div>

          {/* Comparison */}
          <div>
            <div className="text-center text-xl font-bold text-gray-700 mb-2">Comparison</div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {selectedItems.map((selectedItem, index) => (
                <div key={index} className="bg-white p-4 rounded shadow flex flex-col gap-3">
                  <div className="flex justify-center gap-2">
                    <button
                      className={`px-3 py-1 rounded-l ${
                        !comparisonToggles[index] ? "bg-blue-600 text-white" : "bg-white border"
                      }`}
                      onClick={() =>
                        setComparisonToggles((prev) =>
                          prev.map((t, i) => (i === index ? false : t))
                        )
                      }
                    >
                      General
                    </button>
                    <button
                      className={`px-3 py-1 rounded-r ${
                        comparisonToggles[index] ? "bg-blue-600 text-white" : "bg-white border"
                      }`}
                      onClick={() =>
                        setComparisonToggles((prev) =>
                          prev.map((t, i) => (i === index ? true : t))
                        )
                      }
                    >
                      Scientific
                    </button>
                  </div>
                  <div className="bg-gray-100 p-3 rounded text-center text-blue-700 font-bold text-base min-h-[48px]">
                    {selectedItem && inputValue ? (
                      <>
                        {formatNumber(
                          getComparisonValue(selectedItem),
                          comparisonToggles[index]
                        )}{" "}
                        {selectedItem?.expand?.unit?.symbol || ""}
                      </>
                    ) : (
                      "..."
                    )}
                  </div>
                  <RealWorldBox
                    selected={selectedItem}
                    setSelected={(val) =>
                      setSelectedItems((prev) => prev.map((item, i) => (i === index ? val : item)))
                    }
                    items={realWorldItems}
                    scientificToggle={true}
                  />
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* ✅ Prefixes Table Section */}
      {prefixes.length > 0 && (
        <div className="mt-16">
          <h2 className="text-2xl font-bold text-gray-800 mb-4 text-center">Prefixes</h2>
          <div className="overflow-x-auto">
            <table className="min-w-full border border-gray-300 text-sm text-left bg-white shadow">
              <thead className="bg-gray-100 text-gray-700">
                <tr>
                  <th className="px-4 py-2 border">Order</th>
                  <th className="px-4 py-2 border">Name</th>
                  <th className="px-4 py-2 border">Symbol</th>
                  <th className="px-4 py-2 border">Multiplier</th>
                  <th className="px-4 py-2 border">Exponential</th>
                  <th className="px-4 py-2 border">Description</th>
                </tr>
              </thead>
              <tbody>
                {prefixes
                  .sort((a, b) => a.order - b.order)
                  .map((prefix) => (
                    <tr key={prefix.id} className="hover:bg-gray-50">
                      <td className="px-4 py-2 border">{prefix.order}</td>
                      <td className="px-4 py-2 border">{prefix.name}</td>
                      <td className="px-4 py-2 border font-mono">{prefix.symbol}</td>
                      <td className="px-4 py-2 border">{prefix.multiplier}</td>
                      <td className="px-4 py-2 border">{prefix.exponential}</td>
                      <td className="px-4 py-2 border">{prefix.description}</td>
                    </tr>
                  ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
}

export default Converter;
