import React from "react";
import { formatNumber } from "../utils/formatNumber";

function RealWorldBox({ selected, setSelected, items, inputValue, scientific }) {
  const sortedItems = [...items].sort((a, b) => a.scientific_value - b.scientific_value);
  const result = selected && inputValue && selected.scientific_value
    ? inputValue / selected.scientific_value
    : null;

  return (
    <div className="bg-white shadow border rounded-lg p-4 flex flex-col gap-3 overflow-y-auto max-h-64">
      {sortedItems.map((item) => {
        const itemResult = inputValue && item.scientific_value ? inputValue / item.scientific_value : null;
        const isSelected = selected?.id === item.id;

        return (
          <div
            key={item.id}
            onClick={() => setSelected(item)}
            className={`border p-3 rounded cursor-pointer mb-2 ${
              isSelected ? 'bg-blue-50 border-blue-500' : 'bg-gray-50'
            }`}
          >
            <div className="font-semibold text-center mb-1">{item.name}</div>
            <div className="text-sm text-center text-blue-600">
              {formatNumber(itemResult, scientific)}
            </div>
            <div className="text-xs text-center text-gray-600">
              {formatNumber(item.scientific_value, true)} |{" "}
              {formatNumber(parseFloat(item.approx_value), false, true)} {item.expand?.unit?.symbol || ""}
            </div>
            {item.notes && (
              <div className="text-xs italic text-gray-500 mt-1 text-center">
                {item.notes}
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
}

export default RealWorldBox;
