import React from "react";
import { useTheme } from "../context/ThemeContext";

export default function Header() {
  const theme = useTheme();

  return (
    <header
      className="shadow w-full py-4 px-6"
      style={{
        backgroundColor: theme?.headerBg || theme?.surface || "white", // ✅ Use headerBg
        color: theme?.text || "#1f2937",
        fontFamily: theme?.font || "inherit",
      }}
    >
      <div className="max-w-7xl mx-auto flex justify-between items-center">
        {theme?.logo ? (
          <img src={theme.logo} alt="Logo" className="h-12 rounded-full bg-white p-1" />
        ) : (
          <h1
            className="text-2xl font-bold"
            style={{ color: theme?.primary || "#1D4ED8" }}
          >
            Unit Converter
          </h1>
        )}
      </div>
    </header>
  );
}
