import React from "react";
import { formatNumber } from "../utils/formatNumber";

function RealWorldBox({ selected, setSelected, items = [], scientificToggle = true }) {
  const safeItems = Array.isArray(items) ? items : [];

  const sortedItems = [...safeItems].sort((a, b) => {
    const aVal = a?.scientific_value ?? Number.MAX_SAFE_INTEGER;
    const bVal = b?.scientific_value ?? Number.MAX_SAFE_INTEGER;
    return aVal - bVal;
  });

  return (
    <div
      className="overflow-y-auto max-h-[300px] pr-1 flex flex-col gap-3"
      style={{ overflowX: "hidden" }}
    >
      {sortedItems.map((item) => {
        const isSelected = selected?.id === item.id;
        const hasExpression = !!item.expression && !item.approx_value && !item.scientific_value;

        return (
          <div
            key={item.id}
            onClick={() => setSelected?.(item)}
            role="button"
            tabIndex={0}
            onKeyDown={(e) => {
              if (e.key === "Enter" || e.key === " ") setSelected?.(item);
            }}
            className={`cursor-pointer border p-3 rounded shadow-sm transition ${
              isSelected ? "bg-blue-50 border-blue-400" : "bg-white"
            } ${hasExpression ? "border-dashed border-blue-300" : ""}`}
          >
            {/* Row 1: Item Name */}
            <div className="font-semibold text-blue-800 text-center">{item.name}</div>

            {/* Row 2: Display expression or values */}
            <div className="text-sm mt-1 flex justify-between text-blue-600 font-mono">
              {hasExpression ? (
                <>
                  <span className="text-center w-full">{item.expression}</span>
                </>
              ) : (
                <>
                  <span>{formatNumber(item.scientific_value, true)}</span>
                  <span>
                    {formatNumber(item.approx_value, false, true)}{" "}
                    {item?.expand?.unit?.symbol || ""}
                  </span>
                </>
              )}
            </div>

            {/* Row 3: Notes (optional) */}
            {item.notes && (
              <div
                className="prose max-w-none text-xs text-gray-500 italic mt-2 text-center"
                dangerouslySetInnerHTML={{ __html: item.notes }}
              />
            )}
          </div>
        );
      })}
    </div>
  );
}

export default RealWorldBox;
