import React, { useState, useEffect } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { formatNumber } from "../utils/formatNumber";

function Converter({ categoryId }) {
  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [fromUnit, setFromUnit] = useState("");
  const [conversionUnits, setConversionUnits] = useState(["", "", ""]);
  const [selectedRealItems, setSelectedRealItems] = useState([null, null, null]);
  const [conversionToggles, setConversionToggles] = useState([false, false, false]);

  useEffect(() => {
    async function fetchData() {
      try {
        const unitList = await pb.collection("units").getFullList({
          filter: `category = "${categoryId}"`
        });
        const sortedUnits = unitList.sort((a, b) => a.to_base_factor - b.to_base_factor);
        setUnits(sortedUnits);
        setFromUnit(sortedUnits[0]?.id || "");
        setConversionUnits(sortedUnits.slice(0, 3).map((u) => u.id));

        const realItems = await pb.collection("realworld_items").getFullList({
          filter: `category = "${categoryId}"`,
          expand: "unit"
        });
        const sortedReal = realItems.sort((a, b) => a.scientific_value - b.scientific_value);
        setRealWorldItems(sortedReal);
        setSelectedRealItems([sortedReal[0], sortedReal[1], sortedReal[2]]);
      } catch (err) {
        console.error("Error fetching data", err);
      }
    }

    if (categoryId) fetchData();
  }, [categoryId]);

  const getConvertedValue = (toUnitId) => {
    const from = units.find((u) => u.id === fromUnit);
    const to = units.find((u) => u.id === toUnitId);
    if (!from || !to || !inputValue) return null;
    const baseValue = parseFloat(inputValue) * from.to_base_factor;
    return baseValue / to.to_base_factor;
  };

  const updateConversionUnit = (index, unitId) => {
    const updated = [...conversionUnits];
    updated[index] = unitId;
    setConversionUnits(updated);
  };

  const toggleConversion = (index) => {
    const updated = [...conversionToggles];
    updated[index] = !updated[index];
    setConversionToggles(updated);
  };

  const updateRealItem = (index, item) => {
    const updated = [...selectedRealItems];
    updated[index] = item;
    setSelectedRealItems(updated);
  };

  return (
    <div className="flex flex-col sm:flex-row gap-6 bg-white rounded shadow p-6">
      {/* Left: From Block */}
      <div className="sm:w-1/4 flex flex-col justify-center">
        <h2 className="text-xl font-bold mb-2">From</h2>
        <input
          type="number"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          className="border p-2 rounded w-full mb-2"
          placeholder="Enter value"
        />
        <select
          value={fromUnit}
          onChange={(e) => setFromUnit(e.target.value)}
          className="border p-2 rounded w-full"
          size={5}
        >
          {units.map((u) => (
            <option key={u.id} value={u.id}>
              {u.name} ({u.symbol})
            </option>
          ))}
        </select>
      </div>

      {/* Right: Conversions + Comparisons */}
      <div className="sm:w-3/4 flex flex-col gap-8">
        {/* Unit Conversion */}
        <div>
          <h3 className="text-lg font-semibold mb-4">Unit Conversion</h3>
          <div className="flex flex-col sm:flex-row gap-4">
            {[0, 1, 2].map((i) => {
              const unit = units.find((u) => u.id === conversionUnits[i]);
              return (
                <div key={i} className="flex-1">
                  <div className="flex justify-center mb-2 gap-2">
                    <button
                      className={`px-4 py-1 rounded-l ${!conversionToggles[i] ? "bg-blue-600 text-white" : "bg-white border"}`}
                      onClick={() => toggleConversion(i)}
                    >
                      General
                    </button>
                    <button
                      className={`px-4 py-1 rounded-r ${conversionToggles[i] ? "bg-blue-600 text-white" : "bg-white border"}`}
                      onClick={() => toggleConversion(i)}
                    >
                      Scientific
                    </button>
                  </div>
                  <div className="bg-gray-100 p-4 rounded shadow text-center text-blue-700 font-bold text-lg">
                    {unit ? `${formatNumber(getConvertedValue(unit.id), conversionToggles[i])} ${unit.symbol}` : "..."}
                  </div>
                  <select
                    className="border p-2 rounded w-full text-sm mt-2"
                    size={5}
                    value={conversionUnits[i]}
                    onChange={(e) => updateConversionUnit(i, e.target.value)}
                  >
                    {units.map((u) => (
                      <option key={u.id} value={u.id}>
                        {u.name} ({u.symbol}) - {formatNumber(getConvertedValue(u.id), conversionToggles[i])}
                      </option>
                    ))}
                  </select>
                </div>
              );
            })}
          </div>
        </div>

        {/* Real-World Comparison */}
        <div>
          <h3 className="text-lg font-semibold mb-4">Real-World Comparison</h3>
          <div className="comparison-section">
            {[0, 1, 2].map((i) => {
              const selectedItem = selectedRealItems[i];
              const outputValue =
                selectedItem && inputValue && selectedItem.scientific_value
                  ? getConvertedValue(fromUnit) / selectedItem.scientific_value
                  : null;
              return (
                <div key={i} className="comparison-card">
                  <div className="bg-gray-100 p-4 rounded shadow mb-4 text-center text-blue-700 font-bold text-lg">
                    {outputValue !== null
                      ? `${formatNumber(outputValue, true)} ${selectedItem?.expand?.unit?.symbol || ""}`
                      : "..."}
                  </div>
                  <RealWorldBox
                    selected={selectedItem}
                    setSelected={(item) => updateRealItem(i, item)}
                    items={realWorldItems}
                  />
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Converter;
