import React from "react";
import { formatNumber } from "../utils/formatNumber";

export default function ComparisonBox({
  comparisonResult,
  selectedItemId,
  onItemChange,
  itemList,
  displayMode,
  onModeChange,
  index,
}) {
  const selectedItem = itemList.find((item) => item.id === selectedItemId);

  const formattedResult = formatNumber(comparisonResult, displayMode);
  const formattedValue = selectedItem
    ? formatNumber(selectedItem.value, displayMode)
    : "-";

  return (
    <div className="bg-white rounded-2xl shadow p-4 w-full flex flex-col gap-2">
      <div className="text-xl font-semibold text-center min-h-[36px]">
        {formattedResult}
      </div>

      <select
        className="bg-white border rounded px-2 py-1 text-sm"
        value={selectedItemId}
        onChange={(e) => onItemChange(index, e.target.value)}
      >
        {itemList.map((item) => (
          <option key={item.id} value={item.id}>
            {item.name}
          </option>
        ))}
      </select>

      {selectedItem && (
        <div className="text-xs text-gray-600 mt-2 text-center">
          <div>
            <span className="font-medium">Scientific:</span>{" "}
            {formatNumber(selectedItem.value, "scientific")}
          </div>
          <div>
            <span className="font-medium">General:</span>{" "}
            {formatNumber(selectedItem.value, "general")}
          </div>
          {selectedItem.note && (
            <div className="italic mt-1 text-gray-500">{selectedItem.note}</div>
          )}
        </div>
      )}

      <div className="flex justify-center gap-2 mt-2 text-xs">
        <button
          onClick={() => onModeChange(index, "general")}
          className={`px-2 py-1 rounded ${
            displayMode === "general" ? "bg-yellow-400 text-black" : "bg-gray-200"
          }`}
        >
          General
        </button>
        <button
          onClick={() => onModeChange(index, "scientific")}
          className={`px-2 py-1 rounded ${
            displayMode === "scientific" ? "bg-yellow-400 text-black" : "bg-gray-200"
          }`}
        >
          Scientific
        </button>
      </div>
    </div>
  );
}
