import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import AuxiliaryConverter from "./AuxiliaryConverter";
import Converter from "./Converter";
import FunFacts from "./FunFacts";

function CategoryPage({ categoryName }) {
  const [categoryId, setCategoryId] = useState(null); // Main category
  const [speedCategoryId, setSpeedCategoryId] = useState(null);
  const [timeCategoryId, setTimeCategoryId] = useState(null);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const all = await pb.collection("categories").getFullList();

        const main = all.find((c) => c.name === categoryName);
        const speed = all.find((c) => c.name.toLowerCase() === "speed");
        const time = all.find((c) => c.name.toLowerCase() === "time");

        if (main) setCategoryId(main.id);
        if (speed) setSpeedCategoryId(speed.id);
        if (time) setTimeCategoryId(time.id);
      } catch (err) {
        console.error("Error fetching categories:", err);
      }
    };

    if (categoryName) fetchCategories();
  }, [categoryName]);

  if (!categoryId || !speedCategoryId || !timeCategoryId) {
    return <div className="text-center py-10">Loading category...</div>;
  }

  return (
    <div className="space-y-12">
      {/* Auxiliary Section */}
      <div>
        <h2 className="text-2xl font-bold mb-6 text-center text-gray-800">Auxiliary Calculations</h2>
        <div className="flex flex-col lg:flex-row gap-6">
          {/* Time Block */}
          <div className="w-full lg:w-1/2 bg-white p-4 rounded shadow">
            <h3 className="text-xl font-semibold text-center mb-4 text-blue-800">Time</h3>
            <AuxiliaryConverter categoryIds={[timeCategoryId]} />
          </div>

          {/* Speed Block */}
          <div className="w-full lg:w-1/2 bg-white p-4 rounded shadow">
            <h3 className="text-xl font-semibold text-center mb-4 text-blue-800">Speed</h3>
            <AuxiliaryConverter categoryIds={[speedCategoryId]} />
          </div>
        </div>
      </div>

      {/* Main Section */}
      <div>
        <h2 className="text-2xl font-bold mb-4 text-center text-gray-700">Main Calculation</h2>
        <Converter categoryId={categoryId} />
      </div>

      {/* Fun Facts Section */}
      <FunFacts categoryId={categoryId} />
    </div>
  );
}

export default CategoryPage;
