// AuxiliaryBlock.jsx
import React, { useState, useEffect } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { formatNumber } from "../utils/formatNumber";

function AuxiliaryBlock({ categoryName }) {
  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [selectedUnit, setSelectedUnit] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [selectedRealItem, setSelectedRealItem] = useState(null);
  const [categoryId, setCategoryId] = useState("");
  const [scientific, setScientific] = useState(false);
  const [realPage, setRealPage] = useState(0);
  const itemsPerPage = 5;

  useEffect(() => {
    async function fetchCategory() {
      const categories = await pb.collection("categories").getFullList();
      const match = categories.find((c) => c.name === categoryName);
      if (match) setCategoryId(match.id);
    }
    if (categoryName) fetchCategory();
  }, [categoryName]);

  useEffect(() => {
    if (categoryId) {
      pb.collection("units")
        .getFullList({ filter: `category = "${categoryId}"` })
        .then((units) => {
          const sorted = units.sort((a, b) => a.to_base_factor - b.to_base_factor);
          setUnits(sorted);
          setSelectedUnit(sorted[0]?.id || "");
        });

      pb.collection("realworld_items")
        .getFullList({ filter: `category = "${categoryId}"`, expand: "unit" })
        .then((items) => {
          setRealWorldItems(items);
          setSelectedRealItem(items[0]);
        });
    }
  }, [categoryId]);

  const convert = () => {
    const from = units.find((u) => u.id === selectedUnit);
    return from && inputValue ? parseFloat(inputValue) * from.to_base_factor : null;
  };

  const paginatedItems = realWorldItems.slice(
    realPage * itemsPerPage,
    (realPage + 1) * itemsPerPage
  );

  const handleNext = () => {
    const nextPage = realPage + 1;
    if (nextPage * itemsPerPage < realWorldItems.length) {
      setRealPage(nextPage);
    }
  };

  const handlePrev = () => {
    const prevPage = realPage - 1;
    if (prevPage >= 0) {
      setRealPage(prevPage);
    }
  };

  return (
    <div className="bg-white rounded shadow p-4 mb-8">
      <h3 className="text-lg font-bold text-center text-blue-700 mb-4">
        Auxiliary Calculation - {categoryName}
      </h3>

      <div className="flex flex-col sm:flex-row gap-6 mb-6">
        <div className="w-full sm:w-1/2">
          <h4 className="font-medium mb-2 text-center">From</h4>
          <input
            type="number"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            className="border p-2 rounded w-full"
            placeholder="Enter value"
          />
          <select
            value={selectedUnit}
            onChange={(e) => setSelectedUnit(e.target.value)}
            className="border p-2 rounded w-full mt-2"
            size={5} // shows 5 items at a time
          >
            {units.map((u) => (
              <option key={u.id} value={u.id}>
                {u.name} ({u.symbol})
              </option>
            ))}
          </select>
        </div>

        <div className="w-full sm:w-1/2">
          <h4 className="font-medium mb-2 text-center">Comparison</h4>
          <RealWorldBox
            selected={selectedRealItem}
            setSelected={setSelectedRealItem}
            items={paginatedItems}
            inputValue={convert()}
            scientific={scientific}
          />
          <div className="flex justify-between mt-2">
            <button
              onClick={handlePrev}
              disabled={realPage === 0}
              className="text-sm px-2 py-1 border rounded disabled:opacity-50"
            >
              ← Prev
            </button>
            <button
              onClick={handleNext}
              disabled={(realPage + 1) * itemsPerPage >= realWorldItems.length}
              className="text-sm px-2 py-1 border rounded disabled:opacity-50"
            >
              Next →
            </button>
          </div>
        </div>
      </div>

      <div className="flex justify-center gap-4 mt-4">
        <button
          className={`px-4 py-1 rounded-l ${!scientific ? "bg-blue-600 text-white" : "bg-white border"}`}
          onClick={() => setScientific(false)}
        >
          General
        </button>
        <button
          className={`px-4 py-1 rounded-r ${scientific ? "bg-blue-600 text-white" : "bg-white border"}`}
          onClick={() => setScientific(true)}
        >
          Scientific
        </button>
      </div>
    </div>
  );
}

export default AuxiliaryBlock;
