import React, { useState, useEffect } from "react";

const units = [
  "mm³", "cm³", "mL", "in³", "pt (US)", "qt (US)", "L", "gal (US)",
  "ft³", "yd³", "m³", "km³", "mi³"
];

function ConversionBox({ label }) {
  const [value, setValue] = useState("");
  const [fromUnit, setFromUnit] = useState("L");
  const [results, setResults] = useState({});

  useEffect(() => {
    if (!value || isNaN(value)) {
      setResults({});
      return;
    }

    const fetchResults = async () => {
      const resultMap = {};
      for (const to of units) {
        if (to === fromUnit) continue;
        try {
          const res = await fetch(
            `http://localhost:5000/api/convert/volume?value=${value}&from=${encodeURIComponent(fromUnit)}&to=${encodeURIComponent(to)}`
          );
          const data = await res.json();
          resultMap[to] = data.result ?? "Error";
        } catch {
          resultMap[to] = "Error";
        }
      }
      setResults(resultMap);
    };

    fetchResults();
  }, [value, fromUnit]);

  return (
    <div className="border rounded p-4 flex flex-col bg-white shadow-sm h-full">
      <h3 className="text-lg font-semibold text-center mb-2">{label}</h3>
      <input
        type="number"
        className="border p-2 mb-2 rounded"
        placeholder="Enter value"
        value={value}
        onChange={(e) => setValue(e.target.value)}
      />
      <select
        className="border p-2 mb-4 rounded"
        value={fromUnit}
        onChange={(e) => setFromUnit(e.target.value)}
      >
        {units.map((unit) => (
          <option key={unit}>{unit}</option>
        ))}
      </select>

      <div className="overflow-y-auto flex-1 border rounded p-2 bg-gray-50">
        {units
          .filter((u) => u !== fromUnit)
          .map((unit) => (
            <div
              key={unit}
              className="flex justify-between border-b py-1 px-2 text-sm"
            >
              <span className="text-gray-700">{unit}</span>
              <span className="font-medium text-blue-600">
                {results[unit] !== undefined ? results[unit] : "..."}
              </span>
            </div>
          ))}
      </div>
    </div>
  );
}

export default function VolumeComparison() {
  return (
    <div className="max-w-7xl mx-auto p-6 bg-white rounded-lg shadow mt-6">
      <h2 className="text-2xl font-bold text-center mb-6">Compare Volume Conversions</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <ConversionBox label="Comparison 1" />
        <ConversionBox label="Comparison 2" />
        <ConversionBox label="Comparison 3" />
      </div>
    </div>
  );
}
