// src/pages/Home.jsx
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import pb from "../utils/pocketbaseClient";
import AuxiliaryConverter from "../components/AuxiliaryConverter";

// Categories for tabs and grid
const mainCategories = ["length", "temperature", "area", "volume", "mass", "time"];

const allCategories = [
  "acceleration", "angular-acceleration", "angular-velocity", "area", "capacitance",
  "charge", "current", "density", "electric-field", "energy", "force", "frequency",
  "illuminance", "length", "magnetic-flux-density", "mass", "moment-of-inertia", "power",
  "pressure", "resistance", "resistivity", "specific-heat", "speed", "surface-tension",
  "thermal-conductivity", "temperature", "time", "torque", "viscosity", "volume",
  "voltage", "youngs-modulus"
];

// Utility to format slug into readable title
const toTitleCase = (slug) =>
  slug.split("-").map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(" ");

export default function Home() {
  const [activeMainCategory, setActiveMainCategory] = useState(mainCategories[0]);
  const [categoryId, setCategoryId] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchCategoryId() {
      try {
        const res = await pb.collection("categories").getFirstListItem(`slug="${activeMainCategory}"`);
        setCategoryId(res.id);
        console.log("Fetched category ID:", res.id);
      } catch (err) {
        console.error("Error fetching main category ID:", err);
        setCategoryId(null);
      }
    }

    fetchCategoryId();
  }, [activeMainCategory]);

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      {/* Header */}
      <header className="bg-white shadow py-4 px-6">
        <div className="max-w-7xl mx-auto flex items-center justify-between">
          <h1 className="text-2xl font-bold text-blue-700">Unit Converter</h1>
        </div>
      </header>

      {/* Main content */}
      <main className="flex-grow px-4 py-8 max-w-6xl mx-auto">
        {/* Top category buttons */}
        <section className="mb-12">
          <div className="flex flex-wrap justify-center gap-4 mb-6">
            {mainCategories.map((slug) => (
              <button
                key={slug}
                onClick={() => setActiveMainCategory(slug)}
                className={`px-4 py-2 rounded font-semibold border transition ${
                  activeMainCategory === slug
                    ? "bg-blue-600 text-white border-blue-600"
                    : "bg-white text-gray-700 border-gray-300 hover:bg-blue-50"
                }`}
              >
                {toTitleCase(slug)}
              </button>
            ))}
          </div>

          {/* First section: Converter */}
          <div className="bg-white p-6 rounded shadow">
            {categoryId ? (
              <AuxiliaryConverter categoryId={categoryId} />
            ) : (
              <p className="text-center text-gray-500">Loading converter...</p>
            )}
          </div>
        </section>

        {/* Grid of all categories */}
        <section>
          <h2 className="text-xl font-semibold text-center mb-6">All Converters</h2>
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4">
            {allCategories.map((slug) => (
              <button
                key={slug}
                onClick={() => navigate(`/category/${slug}`)}
                className="bg-white shadow hover:bg-blue-50 text-gray-800 font-medium border border-gray-200 rounded px-4 py-3 text-center"
              >
                {toTitleCase(slug)}
              </button>
            ))}
          </div>
        </section>
      </main>

      {/* Footer */}
      <footer className="bg-white shadow py-4 px-6 mt-8">
        <div className="max-w-7xl mx-auto text-center text-gray-500 text-sm">
          Â© {new Date().getFullYear()} Unit Converter. All rights reserved.
        </div>
      </footer>
    </div>
  );
}
