import React, { useState } from "react";
import "./UnitConverter.css";

const units = [
  { name: "Celsius", symbol: "째C" },
  { name: "Fahrenheit", symbol: "째F" },
  { name: "Kelvin", symbol: "K" }
];

const convertTemperature = (value, from, to) => {
  const v = parseFloat(value);
  if (isNaN(v)) return "...";

  if (from === to) return v;

  // Convert to Celsius first
  let celsius;
  if (from === "Celsius") celsius = v;
  else if (from === "Fahrenheit") celsius = (v - 32) * (5 / 9);
  else if (from === "Kelvin") celsius = v - 273.15;

  // Convert from Celsius to target
  if (to === "Celsius") return celsius;
  if (to === "Fahrenheit") return celsius * (9 / 5) + 32;
  if (to === "Kelvin") return celsius + 273.15;

  return "...";
};

function TemperatureConverter({ selectedCategory }) {
  const [inputValue, setInputValue] = useState(0);
  const [fromUnit, setFromUnit] = useState("Celsius");
  const [to1, setTo1] = useState("Fahrenheit");
  const [to2, setTo2] = useState("Kelvin");
  const [to3, setTo3] = useState("Celsius");

  const converted = (toUnit) => convertTemperature(inputValue, fromUnit, toUnit);

  if (selectedCategory !== "Temperature") return null;

  return (
    <div className="max-w-6xl mx-auto px-4 py-6 bg-white rounded-lg shadow">
      <div className="flex flex-col md:flex-row gap-6 items-stretch">
        <div className="md:w-1/4 flex flex-col justify-center">
          <div className="space-y-4">
            <h3 className="text-lg font-semibold text-center">From</h3>
            <input
              type="number"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              className="border p-2 rounded w-full"
              placeholder="Enter temperature"
            />
            <select
              value={fromUnit}
              onChange={(e) => setFromUnit(e.target.value)}
              className="border p-2 rounded w-full"
            >
              {units.map((u) => (
                <option key={u.name} value={u.name}>
                  {u.name} ({u.symbol})
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="md:w-3/4 space-y-10">
          <div>
            <h2 className="text-2xl font-bold mb-4 text-center">Conversion</h2>
            <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
              {[to1, to2, to3].map((id, index) => {
                const setId = index === 0 ? setTo1 : index === 1 ? setTo2 : setTo3;
                return (
                  <div key={index} className="flex flex-col gap-2">
                    <div className="border rounded p-4 bg-white shadow-sm">
                      <div className="text-xl font-bold text-blue-600 mb-2 text-center break-words">
                        {converted(id).toFixed(2)}
                      </div>
                      <select
                        value={id}
                        onChange={(e) => setId(e.target.value)}
                        className="border p-2 rounded w-full text-sm text-center"
                      >
                        <option value="">-- Convert To --</option>
                        {units.map((u) => (
                          <option key={u.name} value={u.name}>
                            {u.name} ({u.symbol})
                          </option>
                        ))}
                      </select>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>

          <div>
            <h2 className="text-2xl font-bold mb-4 text-center">Comparison</h2>
            <div className="text-center text-gray-500 italic">
              Real-world comparison is not applicable for temperature category.
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default TemperatureConverter;
