import React, { useState } from "react";
import AuxiliaryConverter from "../components/AuxiliaryConverter"; // Adjust path as needed
import { useNavigate } from "react-router-dom";

const mainCategories = [
  "Length", "Temperature", "Area", "Volume", "Mass", "Time"
];

const allCategories = [
  "Acceleration", "Angular Acceleration", "Angular Velocity",
  "Area", "Capacitance", "Charge", "Current", "Density", "Electric Field",
  "Energy", "Force", "Frequency", "Illuminance", "Length",
  "Magnetic Flux Density", "Mass", "Moment of Inertia", "Power",
  "Pressure", "Resistance", "Resistivity", "Specific Heat", "Speed",
  "Surface Tension", "Thermal Conductivity", "Temperature", "Time",
  "Torque", "Viscosity", "Volume", "Voltage", "Young's Modulus"
];

export default function Home() {
  const [activeMainCategory, setActiveMainCategory] = useState(mainCategories[0]);
  const navigate = useNavigate();

  return (
    <main className="flex-grow p-6">
      {/* Section 1: Main Categories Tabs */}
      <section className="mb-12">
        <div className="flex space-x-4 justify-center mb-6">
          {mainCategories.map((cat) => (
            <button
              key={cat}
              onClick={() => setActiveMainCategory(cat)}
              className={`px-4 py-2 rounded font-semibold border ${
                activeMainCategory === cat
                  ? "bg-blue-600 text-white border-blue-600"
                  : "bg-white text-gray-700 border-gray-300 hover:bg-blue-50"
              }`}
            >
              {cat}
            </button>
          ))}
        </div>

        {/* Show Auxiliary Calculation for the selected main category */}
        <div className="bg-white p-6 rounded shadow">
          <AuxiliaryConverter categoryName={activeMainCategory} />
        </div>
      </section>

      {/* Section 2: All Converters Tabs */}
      <section>
        <h2 className="text-xl font-semibold text-center mb-6">All Converters</h2>
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4">
          {allCategories.map((cat) => (
            <button
              key={cat}
              onClick={() => navigate(`/category/${encodeURIComponent(cat)}`)}
              className="bg-white shadow hover:bg-blue-50 text-gray-800 font-medium border border-gray-200 rounded px-4 py-3 text-center"
            >
              {cat}
            </button>
          ))}
        </div>
      </section>
    </main>
  );
}
