import React, { useEffect, useState } from "react";
import PocketBase from "pocketbase";
import "./VolumeConverter.css";

const pb = new PocketBase("http://127.0.0.1:8090");

const VolumeConverter = () => {
  const [units, setUnits] = useState([]);
  const [selectedUnit, setSelectedUnit] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [categoryId, setCategoryId] = useState("");

  useEffect(() => {
    const fetchCategoryId = async () => {
      const res = await pb.collection("categories").getFullList();
      const volumeCategory = res.find((cat) => cat.name === "Volume");
      if (volumeCategory) setCategoryId(volumeCategory.id);
    };

    fetchCategoryId();
  }, []);

  useEffect(() => {
    if (categoryId) {
      pb.collection("units")
        .getFullList({ filter: `category = "${categoryId}"` })
        .then(setUnits)
        .catch(console.error);
    }
  }, [categoryId]);

  const getConvertedValues = () => {
    if (!inputValue || !selectedUnit) return [];
    const fromUnit = units.find((u) => u.id === selectedUnit);
    if (!fromUnit) return [];
    return units.map((u) => {
      const converted = (parseFloat(inputValue) * fromUnit.to_base_factor) / u.to_base_factor;
      return { ...u, converted };
    });
  };

  return (
    <div className="converter-container">
      <div className="input-panel">
        <h3>Volume Converter</h3>
        <label>Enter value:</label>
        <input
          type="number"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
        <label>Select from unit:</label>
        <select value={selectedUnit} onChange={(e) => setSelectedUnit(e.target.value)}>
          <option value="">-- Select unit --</option>
          {units.map((unit) => (
            <option key={unit.id} value={unit.id}>
              {unit.name} ({unit.symbol})
            </option>
          ))}
        </select>
      </div>
      <div className="output-panel">
        <h4>Convert To</h4>
        <div className="result-boxes">
          {getConvertedValues().map((u, i) => (
            <div key={u.id} className="result-box">
              <strong>{u.name}</strong>: {u.converted.toFixed(6)} {u.symbol}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default VolumeConverter;
