import React, { useState, useEffect } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { formatNumber } from "../utils/formatNumber";

function Converter({ categoryId }) {
  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [selectedUnit, setSelectedUnit] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [conversionUnits, setConversionUnits] = useState([null, null, null]);
  const [selectedRealItems, setSelectedRealItems] = useState([null, null, null]);
  const [scientificToggles, setScientificToggles] = useState([false, false, false]);
  const [comparisonToggles, setComparisonToggles] = useState([false, false, false]);

  useEffect(() => {
    async function fetchData() {
      try {
        const unitList = await pb.collection("units").getFullList({
          filter: `category = "${categoryId}"`,
        });
        const sortedUnits = unitList.sort((a, b) => a.to_base_factor - b.to_base_factor);
        setUnits(sortedUnits);
        setSelectedUnit(sortedUnits[0]?.id || "");
        setConversionUnits([sortedUnits[1], sortedUnits[2], sortedUnits[3]]);

        const realItems = await pb.collection("realworld_items").getFullList({
          filter: `category = "${categoryId}"`,
          expand: "unit",
        });
        const sortedReal = realItems.sort((a, b) => a.scientific_value - b.scientific_value);
        setRealWorldItems(sortedReal);
        setSelectedRealItems([sortedReal[0], sortedReal[1], sortedReal[2]]);
      } catch (err) {
        console.error("Error fetching units/items", err);
      }
    }

    if (categoryId) fetchData();
  }, [categoryId]);

  const convertToBase = () => {
    const fromUnit = units.find((u) => u.id === selectedUnit);
    return fromUnit && inputValue ? parseFloat(inputValue) * fromUnit.to_base_factor : null;
  };

  const convertToUnit = (baseValue, toUnit) => {
    return baseValue && toUnit ? baseValue / toUnit.to_base_factor : null;
  };

  const updateRealItem = (index, item) => {
    const updated = [...selectedRealItems];
    updated[index] = item;
    setSelectedRealItems(updated);
  };

  const toggleScientific = (index, isConversion = false) => {
    if (isConversion) {
      const updated = [...scientificToggles];
      updated[index] = !updated[index];
      setScientificToggles(updated);
    } else {
      const updated = [...comparisonToggles];
      updated[index] = !updated[index];
      setComparisonToggles(updated);
    }
  };

  const baseValue = convertToBase();

  return (
    <div className="bg-white shadow rounded p-6">
      {/* Input Section */}
      <div className="mb-6">
        <h2 className="text-xl font-bold mb-2">From</h2>
        <input
          type="number"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          className="border p-2 rounded w-full mb-2"
          placeholder="Enter value"
        />
        <select
          value={selectedUnit}
          onChange={(e) => setSelectedUnit(e.target.value)}
          className="border p-2 rounded w-full"
          size={5}
        >
          {units.map((u) => (
            <option key={u.id} value={u.id}>
              {u.name} ({u.symbol})
            </option>
          ))}
        </select>
      </div>

      {/* Unit Conversion */}
      <div className="mb-6">
        <h3 className="text-lg font-semibold mb-2">Unit Conversion</h3>
        <div className="flex flex-col sm:flex-row gap-4">
          {[0, 1, 2].map((i) => (
            <div key={i} className="flex-1 p-4 border rounded shadow bg-gray-50">
              <div className="text-center font-bold text-blue-600 text-xl mb-2">
                {formatNumber(convertToUnit(baseValue, conversionUnits[i]), scientificToggles[i])}
              </div>
              <div className="text-sm text-center mb-2">
                {conversionUnits[i]?.name} ({conversionUnits[i]?.symbol})
              </div>
              <div className="flex justify-center gap-2">
                <button
                  className={`px-3 py-1 rounded-l ${!scientificToggles[i] ? "bg-blue-600 text-white" : "bg-white border"}`}
                  onClick={() => toggleScientific(i, true)}
                >
                  General
                </button>
                <button
                  className={`px-3 py-1 rounded-r ${scientificToggles[i] ? "bg-blue-600 text-white" : "bg-white border"}`}
                  onClick={() => toggleScientific(i, true)}
                >
                  Scientific
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Real World Comparison */}
      <div className="mb-6">
        <h3 className="text-lg font-semibold mb-2">Real World Comparison</h3>
        <div className="flex flex-col sm:flex-row gap-4">
          {[0, 1, 2].map((i) => (
            <div key={i} className="flex-1">
              <RealWorldBox
                selected={selectedRealItems[i]}
                setSelected={(item) => updateRealItem(i, item)}
                items={realWorldItems}
                inputValue={baseValue}
                scientific={comparisonToggles[i]}
              />
              <div className="flex justify-center mt-2 gap-2">
                <button
                  className={`px-4 py-1 rounded-l ${!comparisonToggles[i] ? "bg-blue-600 text-white" : "bg-white border"}`}
                  onClick={() => toggleScientific(i, false)}
                >
                  General
                </button>
                <button
                  className={`px-4 py-1 rounded-r ${comparisonToggles[i] ? "bg-blue-600 text-white" : "bg-white border"}`}
                  onClick={() => toggleScientific(i, false)}
                >
                  Scientific
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Converter;
