import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { formatNumber } from "../utils/formatNumber";
import { useTheme } from "../context/ThemeContext";

function parseScientific(val) {
  if (val === "∞") return Infinity;
  if (!val) return 0;
  const clean = val.replace(/,/g, "").trim();
  const parsed = Number(clean);
  if (!isNaN(parsed)) return parsed;

  const scientificMatch = clean.match(/([0-9.]+)\s*×\s*10\^([-+]?[0-9]+)/);
  if (scientificMatch) {
    const base = parseFloat(scientificMatch[1]);
    const exponent = parseInt(scientificMatch[2], 10);
    return base * Math.pow(10, exponent);
  }

  return 0;
}

function Converter({ categoryId }) {
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [selectedItems, setSelectedItems] = useState(["", "", ""]);
  const { theme } = useTheme();

  useEffect(() => {
    async function fetchRealWorldItems() {
      try {
        const response = await pb.collection("real_world_items").getFullList({
          filter: `category = "${categoryId}"`,
        });

        const filteredItems = response.filter(
          (item) => item.expression !== "∞" && item.expression !== null
        );

        const sortedItems = [...filteredItems].sort((a, b) => {
          return parseScientific(a.expression) - parseScientific(b.expression);
        });

        const finalList = [
          ...sortedItems,
          ...response.filter((item) => item.expression === "∞"),
        ];

        setRealWorldItems(finalList);
        setSelectedItems([
          finalList[0]?.id || "",
          finalList[1]?.id || "",
          finalList[2]?.id || "",
        ]);
      } catch (error) {
        console.error("Error loading data:", error);
      }
    }

    if (categoryId) fetchRealWorldItems();
  }, [categoryId]);

  const handleSelectChange = (index, newId) => {
    const updated = [...selectedItems];
    updated[index] = newId;
    setSelectedItems(updated);
  };

  return (
    <div className="grid md:grid-cols-3 gap-4 w-full">
      {selectedItems.map((selectedId, index) => (
        <div
          key={index}
          className="rounded-2xl p-4"
          style={{ backgroundColor: theme?.boxColor || "#fff" }}
        >
          <RealWorldBox
            selectedItemId={selectedId}
            items={realWorldItems}
            onChange={(id) => handleSelectChange(index, id)}
          />
        </div>
      ))}
    </div>
  );
}

export default Converter;
