import React, { useState } from "react";
import RealWorldDropdown from "./RealWorldDropdown";


const realWorldItems = [
  { name: "Hydrogen Atom (electron cloud)", volume: 6.5e-31 },
  { name: "Virus (e.g., Influenza)", volume: 5e-21 },
  { name: "Red Blood Cell", volume: 9e-17 },
  { name: "Human Egg Cell", volume: 1e-12 },
  { name: "Water Droplet", volume: 5e-8 },
  { name: "Teaspoon", volume: 5e-6 },
  { name: "Golf Ball", volume: 4.1e-5 },
  { name: "Tennis Ball", volume: 1.6e-4 },
  { name: "Coffee Cup", volume: 2e-4 },
  { name: "Milk Carton (1L)", volume: 1e-3 },
  { name: "Cooking Pot (5L)", volume: 0.005 },
  { name: "Pressure Cooker (10L)", volume: 0.01 },
  { name: "Standard Oven", volume: 0.07 },
  { name: "Refrigerator Interior", volume: 0.8 },
  { name: "Car Interior (Compact)", volume: 4 },
  { name: "Small Bedroom", volume: 30 },
  { name: "Shipping Container (20ft)", volume: 33 },
  { name: "Swimming Pool (residential)", volume: 80 },
  { name: "Classroom", volume: 240 },
  { name: "Olympic Swimming Pool", volume: 2500 },
  { name: "Empire State Building", volume: 1e6 },
  { name: "Pyramid of Giza", volume: 2.6e6 },
  { name: "Mount Everest", volume: 5.2e13 },
  { name: "Earth", volume: 1.083e21 },
  { name: "Sun", volume: 1.41e27 },
  { name: "UY Scuti", volume: 6.93e36 }
];

const formatSci = (val) => {
  if (!val || isNaN(val)) return "-";
  return Number(val).toExponential(2).replace("e+", "×10^").replace("e", "×10^");
};
function ItemColumn({ selectedItem, setSelectedItem, volumeM3 }) {
  const result = selectedItem && volumeM3 ? volumeM3 / selectedItem.volume : null;

  return (
    <div className="border rounded p-4 bg-white shadow-sm flex flex-col items-center">
      <div className="text-xl font-bold text-blue-600 mb-2">
        {selectedItem && volumeM3 ? result.toExponential(2).replace("e+", "×10^").replace("e", "×10^") : "..."}
      </div>
      <RealWorldDropdown selected={selectedItem} onSelect={setSelectedItem} />
    </div>
  );
}

export default function PhysicalComparisonFinal({ volumeM3 }) {
  const [selected1, setSelected1] = useState({ name: "Hydrogen Atom (electron cloud)", volume: 6.5e-31 });
  const [selected2, setSelected2] = useState({ name: "Milk Carton (1L)", volume: 1e-3 });
  const [selected3, setSelected3] = useState({ name: "Olympic Swimming Pool", volume: 2500 });

  return (
    <div className="mt-12">
      <h3 className="text-xl font-semibold text-center mb-6">
        Compare with Real-World Objects
      </h3>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <ItemColumn
          selectedItem={selected1}
          setSelectedItem={setSelected1}
          volumeM3={volumeM3}
        />
        <ItemColumn
          selectedItem={selected2}
          setSelectedItem={setSelected2}
          volumeM3={volumeM3}
        />
        <ItemColumn
          selectedItem={selected3}
          setSelectedItem={setSelected3}
          volumeM3={volumeM3}
        />
      </div>
    </div>
  );
}
