import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import { formatNumber } from "../utils/formatNumber";
import { useTheme } from "../context/ThemeContext";

function parseScientific(val) {
  if (val == null) return null;
  const num = parseFloat(val);
  return Number.isNaN(num) ? null : num.toExponential(2);
}

export default function MoleConverter({ categoryId }) {
  const theme = useTheme();
  const [inputValue, setInputValue] = useState("");
  const [realWorldItems, setRealWorldItems] = useState([]);

  useEffect(() => {
    if (!categoryId) return;

    const fetchRealWorldItems = async () => {
      try {
        const records = await pb
          .collection("real_world_items")
          .getFullList({
            filter: `category="${categoryId}"`,
            sort: "+value",
          });
        setRealWorldItems(records);
      } catch (err) {
        console.error("Failed to fetch real-world mole items:", err);
      }
    };

    fetchRealWorldItems();
  }, [categoryId]);

  const handleChange = (e) => {
    setInputValue(e.target.value);
  };

  const value = parseFloat(inputValue);

  return (
    <div className="p-4 space-y-4">
      <h2 className="text-xl font-bold" style={{ color: theme?.mainHeading }}>
        Mole Converter
      </h2>
      <input
        type="number"
        value={inputValue}
        onChange={handleChange}
        className="w-full p-2 border rounded"
        placeholder="Enter value in moles"
      />

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {realWorldItems.map((item) => {
          const ratio = value && item.value ? value / item.value : null;
          return (
            <div
              key={item.id}
              className="p-4 rounded shadow"
              style={{
                backgroundColor: theme?.boxColor || "#ffffff",
                color: theme?.text || "#1f2937",
              }}
            >
              <h3 className="font-semibold text-lg">{item.name}</h3>
              {ratio != null && (
                <p>
                  {formatNumber(ratio)}Ã— {item.name}
                  <br />
                  ({parseScientific(ratio)} scientific)
                </p>
              )}
              <p className="text-sm mt-2 text-gray-500">{item.note}</p>
            </div>
          );
        })}
      </div>
    </div>
  );
}
