import React, { useState, useEffect } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { formatNumber } from "../utils/formatNumber";

function Converter({ categoryId }) {
  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [fromUnit, setFromUnit] = useState("");
  const [toUnits, setToUnits] = useState(["", "", ""]);
  const [conversionToggles, setConversionToggles] = useState([false, false, false]);
  const [comparisonToggles, setComparisonToggles] = useState([false, false, false]);
  const [selectedItems, setSelectedItems] = useState([null, null, null]);

  useEffect(() => {
    async function fetchData() {
      try {
        const unitList = await pb.collection("units").getFullList({
          filter: `category = "${categoryId}"`,
        });
        const sorted = unitList.sort((a, b) => a.to_base_factor - b.to_base_factor);
        setUnits(sorted);
        setFromUnit(sorted[0]?.id || "");
        setToUnits([sorted[1]?.id || "", sorted[2]?.id || "", sorted[3]?.id || ""]);

        const realList = await pb.collection("realworld_items").getFullList({
          filter: `category = "${categoryId}"`,
          expand: "unit",
        });
        const sortedReal = realList.sort((a, b) => a.scientific_value - b.scientific_value);
        setRealWorldItems(sortedReal);
        setSelectedItems([
          sortedReal[0] || null,
          sortedReal[1] || null,
          sortedReal[2] || null,
        ]);
      } catch (err) {
        console.error("Converter fetch error:", err);
      }
    }

    if (categoryId) fetchData();
  }, [categoryId]);

  const getConvertedValue = (toUnitId) => {
    const from = units.find((u) => u.id === fromUnit);
    const to = units.find((u) => u.id === toUnitId);
    if (!from || !to || !inputValue) return null;
    const base = parseFloat(inputValue) * from.to_base_factor;
    return base / to.to_base_factor;
  };

  const getComparisonValue = (index) => {
    const from = units.find((u) => u.id === fromUnit);
    const approx = selectedItems[index]?.approx_value;
    if (!from || !approx || !inputValue) return null;
    const base = parseFloat(inputValue) * from.to_base_factor;
    return base / approx;
  };

  const handleUnitSelect = (unitId) => setFromUnit(unitId);

  return (
    <div className="space-y-6">
      {/* Input & From Unit */}
      <div className="flex flex-col md:flex-row gap-4 justify-center items-center">
        <input
          type="number"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          placeholder="Enter value"
          className="border p-2 rounded w-40"
        />
        <div className="flex flex-wrap gap-2 border p-2 rounded w-full md:w-auto max-w-xl max-h-32 overflow-y-auto">
          {units.map((u) => (
            <div
              key={u.id}
              onClick={() => handleUnitSelect(u.id)}
              className={`cursor-pointer border px-2 py-1 rounded ${
                fromUnit === u.id ? "bg-blue-600 text-white" : "bg-white"
              }`}
            >
              {u.name} ({u.symbol})
            </div>
          ))}
        </div>
      </div>

      {/* Conversion and Comparison Boxes */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {toUnits.map((toUnitId, index) => (
          <div key={index} className="flex flex-col gap-4 bg-white p-4 rounded shadow">
            {/* Conversion Box */}
            <div className="bg-gray-50 p-4 rounded shadow">
              <div className="text-center font-semibold mb-2">Conversion</div>
              <div className="flex justify-center gap-2 mb-2">
                <button
                  className={`px-4 py-1 rounded-l ${
                    !conversionToggles[index] ? "bg-blue-600 text-white" : "bg-white border"
                  }`}
                  onClick={() =>
                    setConversionToggles((prev) => {
                      const updated = [...prev];
                      updated[index] = false;
                      return updated;
                    })
                  }
                >
                  General
                </button>
                <button
                  className={`px-4 py-1 rounded-r ${
                    conversionToggles[index] ? "bg-blue-600 text-white" : "bg-white border"
                  }`}
                  onClick={() =>
                    setConversionToggles((prev) => {
                      const updated = [...prev];
                      updated[index] = true;
                      return updated;
                    })
                  }
                >
                  Scientific
                </button>
              </div>
              <div className="text-center text-blue-700 font-bold text-sm bg-white py-2 rounded border">
                {formatNumber(getConvertedValue(toUnitId), conversionToggles[index])}{" "}
                {units.find((u) => u.id === toUnitId)?.symbol || ""}
              </div>
              <div className="flex flex-wrap gap-2 mt-2 overflow-y-auto max-h-32">
                {units.map((u) => (
                  <div
                    key={u.id}
                    onClick={() =>
                      setToUnits((prev) => {
                        const updated = [...prev];
                        updated[index] = u.id;
                        return updated;
                      })
                    }
                    className={`cursor-pointer border px-2 py-1 rounded text-sm ${
                      toUnits[index] === u.id ? "bg-blue-600 text-white" : "bg-white"
                    }`}
                  >
                    {u.name} ({u.symbol})
                  </div>
                ))}
              </div>
            </div>

            {/* Comparison Box */}
            <div className="bg-gray-50 p-4 rounded shadow">
              <div className="text-center font-semibold mb-2">Comparison</div>
              <div className="flex justify-center gap-2 mb-2">
                <button
                  className={`px-4 py-1 rounded-l ${
                    !comparisonToggles[index] ? "bg-blue-600 text-white" : "bg-white border"
                  }`}
                  onClick={() =>
                    setComparisonToggles((prev) => {
                      const updated = [...prev];
                      updated[index] = false;
                      return updated;
                    })
                  }
                >
                  General
                </button>
                <button
                  className={`px-4 py-1 rounded-r ${
                    comparisonToggles[index] ? "bg-blue-600 text-white" : "bg-white border"
                  }`}
                  onClick={() =>
                    setComparisonToggles((prev) => {
                      const updated = [...prev];
                      updated[index] = true;
                      return updated;
                    })
                  }
                >
                  Scientific
                </button>
              </div>
              <div className="text-center text-blue-700 font-bold text-sm bg-white py-2 rounded border mb-2">
                {selectedItems[index] && inputValue
                  ? `${formatNumber(
                      getComparisonValue(index),
                      comparisonToggles[index]
                    )} ${selectedItems[index]?.expand?.unit?.symbol || ""}`
                  : "..."}
              </div>
              <RealWorldBox
                selected={selectedItems[index]}
                setSelected={(item) =>
                  setSelectedItems((prev) => {
                    const updated = [...prev];
                    updated[index] = item;
                    return updated;
                  })
                }
                items={realWorldItems}
                scientificToggle={true}
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Converter;
