import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import AuxiliaryConverter from "../components/AuxiliaryConverter";
import pb from "../utils/pocketbaseClient";
import Header from "../components/Header";
import Footer from "../components/Footer";

// Categories
const mainCategories = ["length", "temperature", "area", "volume", "mass", "time"];
const allCategories = [
  "acceleration", "angular-acceleration", "angular-velocity", "area", "capacitance", "charge", "current",
  "density", "electric-field", "energy", "force", "frequency", "illuminance", "length", "magnetic-flux-density",
  "mass", "moment-of-inertia", "power", "pressure", "resistance", "resistivity", "specific-heat", "speed",
  "surface-tension", "thermal-conductivity", "temperature", "time", "torque", "viscosity", "volume", "voltage", "youngs-modulus"
];

// Utility to convert slug to readable title
const toTitleCase = (slug) =>
  slug
    .split("-")
    .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
    .join(" ");

export default function Home() {
  const [activeMainCategory, setActiveMainCategory] = useState(mainCategories[0]);
  const [categoryId, setCategoryId] = useState(null);
  const navigate = useNavigate();

  // Fetch categoryId from PocketBase for active main category
  useEffect(() => {
    const fetchCategoryId = async () => {
      try {
        const record = await pb.collection("categories").getFirstListItem(
          `slug="${activeMainCategory}"`
        );
        setCategoryId(record.id);
      } catch (err) {
        console.error("Error fetching categoryId:", err);
        setCategoryId(null);
      }
    };

    fetchCategoryId();
  }, [activeMainCategory]);

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      <Header />

      <main className="flex-grow px-4 py-8 max-w-6xl mx-auto">
        {/* Top category buttons */}
        <section className="mb-12">
          <div className="flex flex-wrap justify-center gap-4 mb-6">
            {mainCategories.map((slug) => (
              <button
                key={slug}
                onClick={() => setActiveMainCategory(slug)}
                className={`px-4 py-2 rounded font-semibold border transition ${
                  activeMainCategory === slug
                    ? "bg-blue-600 text-white border-blue-600"
                    : "bg-white text-gray-700 border-gray-300 hover:bg-blue-50"
                }`}
              >
                {toTitleCase(slug)}
              </button>
            ))}
          </div>

          {/* Converter section with reduced width */}
          <div className="max-w-3xl mx-auto bg-white p-6 rounded shadow">
            {categoryId ? (
              <AuxiliaryConverter categoryId={categoryId} />
            ) : (
              <p className="text-center text-gray-500">Loading converter...</p>
            )}
          </div>
        </section>

        {/* Full-width Grid of all categories */}
        <section>
          <h2 className="text-xl font-semibold text-center mb-6">All Converters</h2>
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4">
            {allCategories.map((slug) => (
              <button
                key={slug}
                onClick={() => window.open(`/category/${slug}`, "_blank")}
                className="bg-white shadow hover:bg-blue-50 text-gray-800 font-medium border border-gray-200 rounded px-4 py-3 text-center"
              >
                {toTitleCase(slug)}
              </button>
            ))}
          </div>
        </section>
      </main>

      <Footer />
    </div>
  );
}
