import React, { useState } from 'react';
import VolumeComparisonFinal from './components/VolumeComparisonFinal';

export default function App() {
  const [activeCategory, setActiveCategory] = useState("Volume");

  const renderCategoryComponent = () => {
    switch (activeCategory) {
      case "Volume":
        return <VolumeComparisonFinal />;
      case "Power":
        return <div className="text-center py-10 text-gray-500">Power converter coming soon...</div>;
      case "Length":
        return <div className="text-center py-10 text-gray-500">Length converter coming soon...</div>;
      default:
        return <div className="text-center py-10 text-gray-500">Select a category to get started.</div>;
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-100">
      {/* Header */}
      <header className="bg-white shadow py-4 px-6">
        <div className="flex flex-col md:flex-row md:items-center md:justify-between">
          <div className="text-2xl font-bold text-blue-600 mb-4 md:mb-0">ðŸ”„ UnitCompare</div>
          <nav className="flex flex-wrap gap-4">
            {["Volume", "Power", "Length"].map((cat) => {
              return (
                <button
                  key={cat}
                  onClick={() => setActiveCategory(cat)}
                  className={`px-4 py-2 rounded ${activeCategory === cat ? "bg-blue-600 text-white" : "bg-gray-200 text-gray-800"}`}
                >
                  {cat}
                </button>
              );
            })}
          </nav>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-grow p-6">
        {renderCategoryComponent()}
      </main>

      {/* Footer */}
      <footer className="bg-white border-t text-center text-sm text-gray-500 py-4 mt-6">
        Â© 2025 UnitCompare. All rights reserved.
      </footer>
    </div>
  );
}
