import React from "react";
import { useTheme } from "../context/ThemeContext";
import { Link } from "react-router-dom";

export default function Header() {
  const theme = useTheme();

  return (
    <header
      className="shadow w-full py-1 px-4"
      style={{
        backgroundColor: theme?.headerBg || theme?.surface || "white",
        color: theme?.text || "#1f2937",
        fontFamily: theme?.font || "inherit",
      }}
    >
      <div className="max-w-7xl mx-auto flex justify-center items-center">
        <Link to="/">
          {theme?.logo ? (
            <img
              src={theme.logo}
              alt="Logo"
              className="h-24 w-24 rounded-full bg-white p-1 object-contain"
            />
          ) : (
            <h1
              className="text-2xl font-bold"
              style={{ color: theme?.primary || "#1D4ED8" }}
            >
              Curiouscockatoo
            </h1>
          )}
        </Link>
      </div>
    </header>
  );
}
