import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { formatNumber, formatNumberString } from "../utils/formatNumber";
import { useTheme } from "../context/ThemeContext";

function parseScientific(val) {
  if (val == null) return null;
  if (typeof val === "number") return val.toExponential(3);
  if (typeof val === "string") {
    const num = Number(val);
    return isNaN(num) ? val : num.toExponential(3);
  }
  return val;
}

const Converter = ({ categoryId }) => {
  const [units, setUnits] = useState([]);
  const [items, setItems] = useState([]);
  const [inputValue, setInputValue] = useState(1);
  const [fromUnit, setFromUnit] = useState(null);
  const [convertedValues, setConvertedValues] = useState([]);
  const [displayMode, setDisplayMode] = useState(["general", "general", "general"]);

  const theme = useTheme();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const unitsData = await pb.collection("units").getFullList({
          filter: `category="${categoryId}"`,
        });

        const itemsData = await pb.collection("realworld_items").getFullList({
          filter: `category="${categoryId}"`,
        });

        setUnits(unitsData.sort((a, b) => a.to_base_factor - b.to_base_factor));
        setItems(itemsData);
        setFromUnit(unitsData[0]); // default
      } catch (err) {
        console.error("Error loading data:", err);
      }
    };

    fetchData();
  }, [categoryId]);

  useEffect(() => {
    if (!fromUnit || units.length === 0) return;

    const baseValue = inputValue * fromUnit.to_base_factor;

    const values = units.map((unit) => {
      const converted = baseValue / unit.to_base_factor;
      return {
        ...unit,
        value: converted,
      };
    });

    setConvertedValues(values);
  }, [inputValue, fromUnit, units]);

  const handleToggleMode = (index) => {
    setDisplayMode((prev) =>
      prev.map((mode, i) => (i === index ? (mode === "general" ? "scientific" : "general") : mode))
    );
  };

  return (
    <div
      className="p-4 rounded-2xl"
      style={{
        backgroundColor: theme?.surface || "#f3f4f6",
        fontFamily: theme?.font,
      }}
    >
      {/* Heading */}
      <h2
        className="text-2xl font-semibold mb-4"
        style={{ color: theme?.primary || "#feb73f" }}
      >
        Converter
      </h2>

      {/* Input & From Unit */}
      <div className="flex flex-col md:flex-row gap-4 items-center mb-6">
        <input
          type="number"
          value={inputValue}
          onChange={(e) => setInputValue(parseFloat(e.target.value))}
          className="border rounded-lg p-2 w-full md:w-1/2"
          style={{
            backgroundColor: theme?.base || "#ffffff",
          }}
        />
        <select
          value={fromUnit?.id || ""}
          onChange={(e) => {
            const selected = units.find((u) => u.id === e.target.value);
            setFromUnit(selected);
          }}
          className="border rounded-lg p-2 w-full md:w-1/2"
          style={{
            backgroundColor: theme?.base || "#ffffff",
          }}
        >
          {units.map((unit) => (
            <option key={unit.id} value={unit.id}>
              {unit.name}
            </option>
          ))}
        </select>
      </div>

      {/* Conversion Boxes */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {[0, 1, 2].map((i) => {
          const unit = convertedValues[i];
          if (!unit) return null;

          return (
            <div
              key={unit.id}
              className="p-4 rounded-xl shadow-md"
              style={{ backgroundColor: theme?.box || "#ffffff" }}
            >
              <div className="flex justify-between items-center mb-2">
                <h3
                  className="text-lg font-semibold"
                  style={{ color: theme?.primary || "#feb73f" }}
                >
                  {unit.name}
                </h3>
                <button
                  onClick={() => handleToggleMode(i)}
                  className="text-sm underline"
                >
                  {displayMode[i] === "general" ? "Scientific" : "General"}
                </button>
              </div>
              <p className="text-xl font-bold">
                {displayMode[i] === "general"
                  ? formatNumber(unit.value)
                  : parseScientific(unit.value)}
              </p>
            </div>
          );
        })}
      </div>

      {/* Real World Comparison */}
      <h2
        className="text-2xl font-semibold mt-8 mb-4"
        style={{ color: theme?.primary || "#feb73f" }}
      >
        Real-World Comparison
      </h2>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {[0, 1, 2].map((i) => (
          <RealWorldBox
            key={i}
            items={items}
            inputValue={inputValue * (fromUnit?.to_base_factor || 1)}
            displayMode={displayMode[i]}
            toggleMode={() => handleToggleMode(i)}
            backgroundColor={theme?.box}
            textColor={theme?.primary}
          />
        ))}
      </div>
    </div>
  );
};

export default Converter;
