import React, { useState, useEffect } from "react";
import PhysicalComparisonFinal from './PhysicalComparisonFinal';

const units = [
  "mm³", "cm³", "mL", "in³", "pt (US)", "qt (US)", "L", "gal (US)",
  "ft³", "yd³", "m³", "km³", "mi³"
];

function ResultColumn({ toUnit, setToUnit, results }) {
  return (
    <div className="flex flex-col gap-4">
      <div className="border rounded p-4 bg-white shadow-sm flex flex-col justify-start items-center">
        <div className="text-xl font-bold text-blue-600 mb-2">
          {toUnit && results[toUnit] !== undefined ? results[toUnit] : "..."}
        </div>
        <select
          className="border p-2 rounded w-full text-center"
          value={toUnit}
          onChange={(e) => setToUnit(e.target.value)}
        >
          {Object.entries(results).map(([unit, value]) => (
            <option key={unit} value={unit}>
              {unit} - {value !== undefined ? value : "..."}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
}

export default function VolumeComparisonFinal() {
  const [fromValue, setFromValue] = useState("");
  const [fromUnit, setFromUnit] = useState("L");
  const [results, setResults] = useState({});
  const [toUnit1, setToUnit1] = useState("gal (US)");
  const [toUnit2, setToUnit2] = useState("m³");
  const [toUnit3, setToUnit3] = useState("km³");

  useEffect(() => {
    if (!fromValue || isNaN(fromValue)) {
      setResults({});
      return;
    }

    const fetchResults = async () => {
      const resultMap = {};
      for (const unit of units) {
        if (unit === fromUnit) continue;
        try {
          const res = await fetch(
            `http://localhost:5000/api/convert/volume?value=${fromValue}&from=${encodeURIComponent(
              fromUnit
            )}&to=${encodeURIComponent(unit)}`
          );
          const data = await res.json();
          resultMap[unit] = data.result ?? "Error";
        } catch {
          resultMap[unit] = "Error";
        }
      }
      setResults(resultMap);
    };

    fetchResults();
  }, [fromValue, fromUnit]);

  return (
    <div className="max-w-6xl mx-auto p-6 bg-white rounded-lg shadow mt-6">
      {/* Convert To */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 items-start mb-10">
        <ResultColumn toUnit={toUnit1} setToUnit={setToUnit1} results={results} />
        <ResultColumn toUnit={toUnit2} setToUnit={setToUnit2} results={results} />
        <ResultColumn toUnit={toUnit3} setToUnit={setToUnit3} results={results} />
      </div>

      {/* From Section Centered */}
      <div className="flex flex-col items-center space-y-3 my-12">
        <div className="text-lg font-semibold text-center">From</div>
        <input
          type="number"
          placeholder="Enter value"
          value={fromValue}
          onChange={(e) => setFromValue(e.target.value)}
          className="border p-2 rounded w-64 text-center"
        />
        <select
          value={fromUnit}
          onChange={(e) => setFromUnit(e.target.value)}
          className="border p-2 rounded w-64 text-center"
        >
          {units.map((unit) => (
            <option key={unit}>{unit}</option>
          ))}
        </select>
      </div>

      {/* Compare To */}
      <h3 className="text-lg font-semibold text-center mb-4">Compare To</h3>
      <PhysicalComparisonFinal volumeM3={results["m³"]} />
    </div>
  );
}
