import React, { useState, useEffect } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { formatNumber } from "../utils/formatNumber";

function AuxiliaryConverter({ categoryIds, title }) {
  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [fromUnit, setFromUnit] = useState("");
  const [toUnit, setToUnit] = useState("");
  const [selectedItem, setSelectedItem] = useState(null);
  const [conversionToggle, setConversionToggle] = useState(false);
  const [comparisonToggle, setComparisonToggle] = useState(false);

  useEffect(() => {
    async function fetchData() {
      try {
        const unitList = await pb.collection("units").getFullList();
        const filteredUnits = unitList.filter((u) =>
          categoryIds.includes(u.category)
        );
        const sortedUnits = filteredUnits.sort(
          (a, b) => a.to_base_factor - b.to_base_factor
        );
        setUnits(sortedUnits);
        setFromUnit(sortedUnits[0]?.id || "");
        setToUnit(sortedUnits[1]?.id || "");

        const itemList = await pb.collection("realworld_items").getFullList({
          expand: "unit",
        });
        const filteredItems = itemList.filter((item) =>
          categoryIds.includes(item.category)
        );
        const sortedItems = filteredItems.sort(
          (a, b) => a.scientific_value - b.scientific_value
        );
        setRealWorldItems(sortedItems);
        setSelectedItem(sortedItems[0] || null);
      } catch (err) {
        console.error("Auxiliary fetch error:", err);
      }
    }

    if (categoryIds?.length) fetchData();
  }, [categoryIds]);

  const getConvertedValue = () => {
    const from = units.find((u) => u.id === fromUnit);
    const to = units.find((u) => u.id === toUnit);
    if (!from || !to || !inputValue) return null;
    const baseValue = parseFloat(inputValue) * from.to_base_factor;
    return baseValue / to.to_base_factor;
  };

  const getComparisonValue = () => {
    const from = units.find((u) => u.id === fromUnit);
    const inputNum = Number(inputValue);
    const approx = selectedItem?.approx_value;
    if (!from || isNaN(inputNum) || !approx) return null;
    const baseValue = inputNum * from.to_base_factor;
    return baseValue / approx;
  };

  return (
    <div className="bg-white rounded shadow p-6 space-y-6">
      {title && (
        <h3 className="text-xl font-bold text-center text-gray-700">{title}</h3>
      )}

      {/* From Block - Full Width */}
      <div className="flex flex-col sm:flex-row gap-4 justify-center items-center">
        <input
          type="number"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          placeholder="Enter value"
          className="border p-2 rounded w-40"
        />
        <select
          value={fromUnit}
          onChange={(e) => setFromUnit(e.target.value)}
          className="border p-2 rounded w-48"
        >
          {units.map((u) => (
            <option key={u.id} value={u.id}>
              {u.name} ({u.symbol})
            </option>
          ))}
        </select>
      </div>

      {/* Conversion & Comparison side-by-side */}
      <div className="flex flex-col sm:flex-row gap-6 justify-center">
        {/* Conversion Box */}
        <div className="w-full sm:w-1/2 bg-white p-4 rounded shadow">
          <div className="text-center text-lg font-semibold mb-2">Conversion</div>
          <div className="flex justify-center gap-2 mb-2">
            <button
              className={`px-4 py-1 rounded-l ${
                !conversionToggle ? "bg-blue-600 text-white" : "bg-white border"
              }`}
              onClick={() => setConversionToggle(false)}
            >
              General
            </button>
            <button
              className={`px-4 py-1 rounded-r ${
                conversionToggle ? "bg-blue-600 text-white" : "bg-white border"
              }`}
              onClick={() => setConversionToggle(true)}
            >
              Scientific
            </button>
          </div>
          <div className="bg-gray-100 p-4 rounded text-center text-blue-700 font-bold text-lg">
            {toUnit ? (
              <>
                {formatNumber(getConvertedValue(), conversionToggle)}{" "}
                {units.find((u) => u.id === toUnit)?.symbol || ""}
              </>
            ) : (
              "..."
            )}
          </div>
          <select
            className="border p-2 rounded w-full text-sm mt-2"
            value={toUnit}
            onChange={(e) => setToUnit(e.target.value)}
          >
            {units.map((u) => (
              <option key={u.id} value={u.id}>
                {u.name} ({u.symbol}) -{" "}
                {formatNumber(getConvertedValue(), conversionToggle)}
              </option>
            ))}
          </select>
        </div>

        {/* Comparison Box */}
        <div className="w-full sm:w-1/2 bg-white p-4 rounded shadow">
          <div className="text-center text-lg font-semibold mb-2">Comparison</div>
          <div className="flex justify-center gap-2 mb-2">
            <button
              className={`px-4 py-1 rounded-l ${
                !comparisonToggle ? "bg-blue-600 text-white" : "bg-white border"
              }`}
              onClick={() => setComparisonToggle(false)}
            >
              General
            </button>
            <button
              className={`px-4 py-1 rounded-r ${
                comparisonToggle ? "bg-blue-600 text-white" : "bg-white border"
              }`}
              onClick={() => setComparisonToggle(true)}
            >
              Scientific
            </button>
          </div>
          <div className="bg-gray-100 p-4 rounded mb-4 text-center text-blue-700 font-bold text-lg">
            {selectedItem && inputValue ? (
              <>
                {formatNumber(getComparisonValue(), comparisonToggle)}{" "}
                {selectedItem?.expand?.unit?.symbol || ""}
              </>
            ) : (
              "..."
            )}
          </div>

          <RealWorldBox
            selected={selectedItem}
            setSelected={setSelectedItem}
            items={realWorldItems}
            scientificToggle={true}
          />
        </div>
      </div>
    </div>
  );
}

export default AuxiliaryConverter;
