import React, { useState, useEffect } from "react";
import PocketBase from "pocketbase";

// Connect to PocketBase
const pb = new PocketBase("http://127.0.0.1:8090"); // Change if hosted

export default function VolumeComparisonFinal() {
  const [units, setUnits] = useState([]);
  const [fromValue, setFromValue] = useState("");
  const [fromUnit, setFromUnit] = useState("");
  const [toUnit1, setToUnit1] = useState("");
  const [toUnit2, setToUnit2] = useState("");
  const [toUnit3, setToUnit3] = useState("");
  const [results, setResults] = useState({});

  // Fetch units from PocketBase
  useEffect(() => {
    const fetchUnits = async () => {
      try {
        const res = await pb.collection("units").getFullList({
          filter: 'category="Volume"',
          sort: "name"
        });
        setUnits(res);
        // Default selections if not set
        if (res.length > 0) {
          setFromUnit(res[0].symbol);
          setToUnit1(res[1]?.symbol || "");
          setToUnit2(res[2]?.symbol || "");
          setToUnit3(res[3]?.symbol || "");
        }
      } catch (err) {
        console.error("Error fetching units:", err);
      }
    };

    fetchUnits();
  }, []);

  // Perform conversion
  useEffect(() => {
    if (!fromValue || isNaN(fromValue)) {
      setResults({});
      return;
    }

    const from = units.find((u) => u.symbol === fromUnit);
    if (!from) return;

    const newResults = {};
    [toUnit1, toUnit2, toUnit3].forEach((symbol) => {
      const to = units.find((u) => u.symbol === symbol);
      if (to) {
        const result = (parseFloat(fromValue) * from.factor_to_base) / to.factor_to_base;
        newResults[symbol] = result.toExponential(6);
      }
    });

    setResults(newResults);
  }, [fromValue, fromUnit, toUnit1, toUnit2, toUnit3, units]);

  const renderResultColumn = (label, unitSymbol, setUnitSymbol) => (
    <div className="border rounded p-4 bg-white shadow-sm flex flex-col items-center">
      <div className="text-xl font-bold text-blue-600 mb-2">
        {results[unitSymbol] || "..."}
      </div>
      <select
        className="border p-2 rounded w-full text-center"
        value={unitSymbol}
        onChange={(e) => setUnitSymbol(e.target.value)}
      >
        {units.map((u) => (
          <option key={u.id} value={u.symbol}>
            {u.name} ({u.symbol})
          </option>
        ))}
      </select>
    </div>
  );

  return (
    <div className="max-w-6xl mx-auto p-6 bg-white rounded-lg shadow mt-6">
      {/* Headings */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 items-start mb-2">
        <div></div>
        <div className="md:col-span-3 text-center text-lg font-semibold">
          Convert To
        </div>
      </div>

      {/* Input + Conversion Columns */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 items-start">
        {/* Input Column */}
        <div className="space-y-4">
          <div className="text-lg font-semibold text-center mb-2">From</div>
          <input
            type="number"
            placeholder="Enter value"
            value={fromValue}
            onChange={(e) => setFromValue(e.target.value)}
            className="border p-2 rounded w-full"
          />
          <select
            value={fromUnit}
            onChange={(e) => setFromUnit(e.target.value)}
            className="border p-2 rounded w-full"
          >
            {units.map((u) => (
              <option key={u.id} value={u.symbol}>
                {u.name} ({u.symbol})
              </option>
            ))}
          </select>
        </div>

        {/* Convert Columns */}
        {renderResultColumn("To 1", toUnit1, setToUnit1)}
        {renderResultColumn("To 2", toUnit2, setToUnit2)}
        {renderResultColumn("To 3", toUnit3, setToUnit3)}
      </div>
    </div>
  );
}
