// Converter.jsx
import React, { useState, useEffect } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import RealWorldGrid from "./RealWorldGrid";
import { formatNumber } from "../utils/formatNumber";
import "./UnitConverter.css";

function Converter({ categoryId, compact = false }) {
  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [selectedFromUnit, setSelectedFromUnit] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [toUnit, setToUnit] = useState("");
  const [toUnits, setToUnits] = useState(["", "", ""]);

  useEffect(() => {
    if (!categoryId) return;

    pb.collection("units")
      .getFullList({ filter: `category = "${categoryId}"` })
      .then((units) => {
        const sorted = [...units].sort((a, b) => a.to_base_factor - b.to_base_factor);
        setUnits(sorted);
        setSelectedFromUnit(sorted[0]?.id || "");

        if (compact) {
          setToUnit(sorted[1]?.id || "");
        } else {
          setToUnits([sorted[0]?.id || "", sorted[1]?.id || "", sorted[2]?.id || ""]);
        }
      });

    pb.collection("realworld_items")
      .getFullList({ filter: `category = "${categoryId}"`, expand: "unit" })
      .then((items) => setRealWorldItems(items));
  }, [categoryId]);

  const convert = (toUnitId) => {
    if (!inputValue || !selectedFromUnit || !toUnitId) return "...";
    const from = units.find((u) => u.id === selectedFromUnit);
    const to = units.find((u) => u.id === toUnitId);
    if (!from || !to) return "...";
    const result = (parseFloat(inputValue) * from.to_base_factor) / to.to_base_factor;
    return formatNumber(result, false);
  };

  const inputToBase = () => {
    const from = units.find((u) => u.id === selectedFromUnit);
    return from && inputValue ? parseFloat(inputValue) * from.to_base_factor : null;
  };

  return (
    <div className="bg-white shadow rounded p-6">
      <div className="mb-4 grid grid-cols-1 md:grid-cols-2 gap-4">
        <input
          type="number"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          className="border p-2 rounded"
          placeholder="Enter value"
        />
        <select
          value={selectedFromUnit}
          onChange={(e) => setSelectedFromUnit(e.target.value)}
          className="border p-2 rounded"
        >
          {units.map((u) => (
            <option key={u.id} value={u.id}>{u.name} ({u.symbol})</option>
          ))}
        </select>
      </div>

      <div className="mb-6">
        <h3 className="text-lg font-bold mb-2">Converted Value</h3>
        {compact ? (
          <div className="border p-4 rounded">
            <div className="text-xl font-bold mb-2 text-blue-600">
              {convert(toUnit)}
            </div>
            <select
              value={toUnit}
              onChange={(e) => setToUnit(e.target.value)}
              className="border p-2 rounded w-full"
            >
              {units.map((u) => (
                <option key={u.id} value={u.id}>{u.name} ({u.symbol})</option>
              ))}
            </select>
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
            {toUnits.map((id, index) => (
              <div key={index} className="border p-4 rounded">
                <div className="text-xl font-bold mb-2 text-blue-600">
                  {convert(id)}
                </div>
                <select
                  value={id}
                  onChange={(e) => {
                    const updated = [...toUnits];
                    updated[index] = e.target.value;
                    setToUnits(updated);
                  }}
                  className="border p-2 rounded w-full"
                >
                  {units.map((u) => (
                    <option key={u.id} value={u.id}>{u.name} ({u.symbol})</option>
                  ))}
                </select>
              </div>
            ))}
          </div>
        )}
      </div>

      {!compact ? (
        <div>
          <h3 className="text-lg font-bold mb-4">Real World Comparison</h3>
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
            <RealWorldBox items={realWorldItems} inputValue={inputToBase()} />
          </div>
        </div>
      ) : (
        <div>
          <h3 className="text-lg font-bold mb-4">Real World Comparison</h3>
          <RealWorldGrid items={realWorldItems} inputValue={inputToBase()} />
        </div>
      )}
    </div>
  );
}

export default Converter;
