import React, { useState, useEffect } from "react";
import pb from "../pocketbase";

// Units sorted from smallest to largest
const volumeUnits = [
  { name: "nm³", factor: 1e-27 },
  { name: "µm³", factor: 1e-18 },
  { name: "mm³", factor: 1e-9 },
  { name: "cm³", factor: 1e-6 },
  { name: "m³", factor: 1 },
  { name: "km³", factor: 1e9 },
];

const VolumeConverter = () => {
  const [inputValue, setInputValue] = useState("");

  const [fromUnit, setFromUnit] = useState("");
  const [toUnit1, setToUnit1] = useState("");
  const [toUnit2, setToUnit2] = useState("");
  const [toUnit3, setToUnit3] = useState("");

  const [converted1, setConverted1] = useState("");
  const [converted2, setConverted2] = useState("");
  const [converted3, setConverted3] = useState("");

  const [comparisonItems, setComparisonItems] = useState([]);
  const [selectedComparison1, setSelectedComparison1] = useState(null);
  const [selectedComparison2, setSelectedComparison2] = useState(null);
  const [selectedComparison3, setSelectedComparison3] = useState(null);

  const [showScientific, setShowScientific] = useState(true);
  const lowestUnit = volumeUnits[0].name;

  const [autoSelected, setAutoSelected] = useState({
    from: false,
    to1: false,
    to2: false,
    to3: false,
  });

  // ✅ Apply lowest unit once per dropdown on first load
  useEffect(() => {
    if (!autoSelected.from && !fromUnit) {
      setFromUnit(lowestUnit);
      setAutoSelected(prev => ({ ...prev, from: true }));
    }
    if (!autoSelected.to1 && !toUnit1) {
      setToUnit1(lowestUnit);
      setAutoSelected(prev => ({ ...prev, to1: true }));
    }
    if (!autoSelected.to2 && !toUnit2) {
      setToUnit2(lowestUnit);
      setAutoSelected(prev => ({ ...prev, to2: true }));
    }
    if (!autoSelected.to3 && !toUnit3) {
      setToUnit3(lowestUnit);
      setAutoSelected(prev => ({ ...prev, to3: true }));
    }
  }, [fromUnit, toUnit1, toUnit2, toUnit3, autoSelected, lowestUnit]);

  // ✅ Fetch real-world volume objects from PocketBase
  useEffect(() => {
    const fetchData = async () => {
      const records = await pb.collection("real_world_objects").getFullList({
        filter: 'category="Volume"',
        sort: "+value_normal",
      });
      setComparisonItems(records);
      setSelectedComparison1(records[0]);
      setSelectedComparison2(records[1]);
      setSelectedComparison3(records[2]);
    };
    fetchData();
  }, []);

  // ✅ Conversion logic
  const convert = (value, from, to) => {
    const fromFactor = volumeUnits.find(u => u.name === from)?.factor || 1;
    const toFactor = volumeUnits.find(u => u.name === to)?.factor || 1;
    return (value * fromFactor) / toFactor;
  };

  // ✅ Perform conversions whenever input or units change
  useEffect(() => {
    const value = parseFloat(inputValue);
    if (!inputValue || isNaN(value)) {
      setConverted1("");
      setConverted2("");
      setConverted3("");
      return;
    }
    setConverted1(convert(value, fromUnit, toUnit1));
    setConverted2(convert(value, fromUnit, toUnit2));
    setConverted3(convert(value, fromUnit, toUnit3));
  }, [inputValue, fromUnit, toUnit1, toUnit2, toUnit3]);

  // ✅ Compare with real-world volume object
  const compareWithRealWorld = (input, item) => {
    if (!input || !item?.value_normal) return 0;
    return parseFloat(input) / parseFloat(item.value_normal);
  };

  const formatValue = (value) => {
    if (!value && value !== 0) return "--";
    return showScientific ? value.toExponential(3) : value.toLocaleString();
  };

  const renderConversionBox = (value, unit, setUnit) => (
    <div style={boxStyle}>
      <div style={valueStyle}>{formatValue(value)}</div>
      <select value={unit} onChange={(e) => setUnit(e.target.value)} style={dropdownStyle}>
        {volumeUnits.map((u) => (
          <option key={u.name} value={u.name}>{u.name}</option>
        ))}
      </select>
    </div>
  );

  const renderComparisonBox = (selected, setSelected) => {
    const ratio = compareWithRealWorld(inputValue, selected);
    return (
      <div style={boxStyle}>
        <div style={valueStyle}>{formatValue(ratio)} ×</div>
        <select
          value={selected?.id}
          onChange={(e) => setSelected(comparisonItems.find(i => i.id === e.target.value))}
          style={dropdownStyle}
        >
          {comparisonItems.map(item => (
            <option key={item.id} value={item.id}>
              {item.name}
            </option>
          ))}
        </select>
        <div style={{ fontSize: "12px", color: "#555", marginTop: "6px" }}>
          {selected?.value_normal} {selected?.unit} ({selected?.value_scientific})
          <br />
          <em>{selected?.note}</em>
        </div>
      </div>
    );
  };

  return (
    <div style={{ padding: "30px", maxWidth: "1000px", margin: "0 auto" }}>
      <h2>Volume Converter</h2>

      {/* Input Section */}
      <div style={{ display: "flex", alignItems: "center", marginBottom: "25px" }}>
        <input
          type="number"
          placeholder="Enter volume"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          style={{ padding: "8px", width: "200px", marginRight: "20px" }}
        />
        <select value={fromUnit} onChange={(e) => setFromUnit(e.target.value)} style={dropdownStyle}>
          {volumeUnits.map((u) => (
            <option key={u.name} value={u.name}>{u.name}</option>
          ))}
        </select>
        <button onClick={() => setShowScientific(!showScientific)} style={{ marginLeft: "20px", padding: "8px" }}>
          Toggle: {showScientific ? "Scientific" : "General"}
        </button>
      </div>

      {/* Conversion Boxes */}
      <div style={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: "20px", marginBottom: "40px" }}>
        {renderConversionBox(converted1, toUnit1, setToUnit1)}
        {renderConversionBox(converted2, toUnit2, setToUnit2)}
        {renderConversionBox(converted3, toUnit3, setToUnit3)}
      </div>

      {/* Comparison Boxes */}
      <h3>Real-World Comparisons</h3>
      <div style={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: "20px" }}>
        {renderComparisonBox(selectedComparison1, setSelectedComparison1)}
        {renderComparisonBox(selectedComparison2, setSelectedComparison2)}
        {renderComparisonBox(selectedComparison3, setSelectedComparison3)}
      </div>
    </div>
  );
};

// ✅ Simple styling
const boxStyle = {
  border: "1px solid #ccc",
  padding: "15px",
  borderRadius: "12px",
  minHeight: "130px",
  background: "#fafafa",
};

const valueStyle = {
  fontWeight: "bold",
  fontSize: "18px",
  marginBottom: "10px",
};

const dropdownStyle = {
  width: "100%",
  padding: "6px",
};

export default VolumeConverter;
