import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import { formatNumber } from "../utils/formatNumber";
import { useTheme } from "../context/ThemeContext";

function parseScientific(val) {
  if (val == null) return null;
  const num = Number(val);
  if (isNaN(num)) return null;
  return num.toExponential(3);
}

export default function MoleConverter() {
  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [fromUnit, setFromUnit] = useState("");
  const [convertedValues, setConvertedValues] = useState([]);
  const [comparisonResults, setComparisonResults] = useState([]);
  const [displayMode, setDisplayMode] = useState(["general", "general", "general"]);
  const [compareMode, setCompareMode] = useState(["general", "general", "general"]);
  const theme = useTheme();

  // Fetch data on mount
  useEffect(() => {
    const fetchData = async () => {
      try {
        const category = await pb.collection("categories").getFirstListItem(`name="Mole"`);
        const [unitRecords, realItems] = await Promise.all([
          pb.collection("units").getFullList({ filter: `category="${category.id}"` }),
          pb.collection("real_world_items").getFullList({ filter: `category="${category.id}"` }),
        ]);

        const sortedUnits = unitRecords.sort((a, b) => a.value - b.value);
        const sortedItems = realItems.sort((a, b) => a.value - b.value);

        setUnits(sortedUnits);
        setFromUnit(sortedUnits[0]?.name || "");
        setRealWorldItems(sortedItems);
      } catch (error) {
        console.error("Error fetching Mole category data:", error);
      }
    };
    fetchData();
  }, []);

  // Convert input when inputValue or fromUnit changes
  useEffect(() => {
    if (!inputValue || !fromUnit || units.length === 0) return;

    const from = units.find((u) => u.name === fromUnit);
    if (!from) return;

    const baseValue = parseFloat(inputValue) * from.value;

    const conversions = units.map((unit) => ({
      name: unit.name,
      general: (baseValue / unit.value).toFixed(6),
      scientific: parseScientific(baseValue / unit.value),
    }));

    setConvertedValues(conversions);

    const comparisons = realWorldItems.map((item) => ({
      name: item.name,
      general: (baseValue / item.value).toFixed(6),
      scientific: parseScientific(baseValue / item.value),
      note: item.note,
    }));

    setComparisonResults(comparisons);
  }, [inputValue, fromUnit, units, realWorldItems]);

  return (
    <div className="p-4">
      <h2 className="text-2xl font-semibold mb-4" style={{ color: theme?.heading }}>
        Mole Converter
      </h2>

      {/* Input Section */}
      <div className="flex flex-col md:flex-row gap-4 items-center mb-6">
        <input
          type="number"
          placeholder="Enter value"
          className="p-2 rounded border w-full md:w-1/3"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
        <select
          value={fromUnit}
          onChange={(e) => setFromUnit(e.target.value)}
          className="p-2 rounded border w-full md:w-1/3"
        >
          {units.map((unit) => (
            <option key={unit.id} value={unit.name}>
              {unit.name}
            </option>
          ))}
        </select>
      </div>

      {/* Output Conversion Boxes */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-10">
        {[0, 1, 2].map((i) => (
          <div
            key={i}
            className="p-4 rounded shadow"
            style={{
              backgroundColor: theme?.box || "#ffffff",
              color: theme?.text || "#000000",
            }}
          >
            <div className="text-xl font-bold mb-2">
              {convertedValues[i] &&
                (displayMode[i] === "general"
                  ? formatNumber(convertedValues[i].general)
                  : convertedValues[i].scientific)}
            </div>
            <select
              value={convertedValues[i]?.name || ""}
              onChange={(e) => {
                const newUnit = e.target.value;
                setFromUnit(newUnit);
              }}
              className="w-full p-2 rounded border"
            >
              {units.map((unit) => (
                <option key={unit.id} value={unit.name}>
                  {unit.name}
                </option>
              ))}
            </select>
            <button
              className="mt-2 text-sm underline"
              onClick={() =>
                setDisplayMode((prev) =>
                  prev.map((mode, idx) => (idx === i ? (mode === "general" ? "scientific" : "general") : mode))
                )
              }
            >
              Toggle to {displayMode[i] === "general" ? "Scientific" : "General"}
            </button>
          </div>
        ))}
      </div>

      {/* Real World Comparison Boxes */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {[0, 1, 2].map((i) => (
          <div
            key={i}
            className="p-4 rounded shadow"
            style={{
              backgroundColor: theme?.box || "#ffffff",
              color: theme?.text || "#000000",
            }}
          >
            <div className="text-xl font-bold mb-2">
              {comparisonResults[i] &&
                (compareMode[i] === "general"
                  ? formatNumber(comparisonResults[i].general)
                  : comparisonResults[i].scientific)}
            </div>
            <select
              className="w-full p-2 rounded border"
              value={comparisonResults[i]?.name || ""}
              onChange={(e) => {
                const selected = realWorldItems.find((item) => item.name === e.target.value);
                if (selected) {
                  const updated = [...comparisonResults];
                  const baseValue =
                    parseFloat(inputValue) * (units.find((u) => u.name === fromUnit)?.value || 1);
                  updated[i] = {
                    name: selected.name,
                    general: (baseValue / selected.value).toFixed(6),
                    scientific: parseScientific(baseValue / selected.value),
                    note: selected.note,
                  };
                  setComparisonResults(updated);
                }
              }}
            >
              {realWorldItems.map((item) => (
                <option key={item.id} value={item.name}>
                  {item.name}
                </option>
              ))}
            </select>
            {comparisonResults[i]?.note && (
              <div className="mt-2 text-sm italic">{comparisonResults[i].note}</div>
            )}
            <button
              className="mt-2 text-sm underline"
              onClick={() =>
                setCompareMode((prev) =>
                  prev.map((mode, idx) => (idx === i ? (mode === "general" ? "scientific" : "general") : mode))
                )
              }
            >
              Toggle to {compareMode[i] === "general" ? "Scientific" : "General"}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
