import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { formatNumber, formatNumberString } from "../utils/formatNumber";
import { useTheme } from "../context/ThemeContext";

// ✅ Inline parseScientific function
function parseScientific(val) {
  if (val == null) return null;
  if (typeof val === "number") return val;
  if (typeof val === "object" && val.$numberDecimal) {
    return Number(val.$numberDecimal);
  }
  const parsed = Number(val);
  return isNaN(parsed) ? null : parsed;
}

function Converter({ categoryId }) {
  const theme = useTheme();

  const [inputValue, setInputValue] = useState(1);
  const [units, setUnits] = useState([]);
  const [items, setItems] = useState([]);
  const [inputUnit, setInputUnit] = useState(null);
  const [outputUnits, setOutputUnits] = useState([]);
  const [comparisonItems, setComparisonItems] = useState([]);
  const [viewMode, setViewMode] = useState("general");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const unitRes = await pb.collection("units").getFullList({
          filter: `category="${categoryId}"`,
        });
        const itemRes = await pb.collection("real_world_items").getFullList({
          filter: `category="${categoryId}"`,
        });

        const sortedUnits = unitRes.sort(
          (a, b) => parseScientific(a.to_base_factor) - parseScientific(b.to_base_factor)
        );

        const sortedItems = itemRes.sort(
          (a, b) => parseScientific(a.value) - parseScientific(b.value)
        );

        setUnits(sortedUnits);
        setItems(sortedItems);
        setInputUnit(sortedUnits[0]);
        setOutputUnits(sortedUnits.slice(0, 3));
        setComparisonItems(sortedItems.slice(0, 3));
      } catch (error) {
        console.error("Error loading data:", error);
      }
    };

    fetchData();
  }, [categoryId]);

  const handleInputChange = (e) => {
    const val = parseFloat(e.target.value);
    if (!isNaN(val)) setInputValue(val);
  };

  const convert = (value, from, to) => {
    if (!from || !to) return null;
    return (value * parseScientific(from.to_base_factor)) / parseScientific(to.to_base_factor);
  };

  const getConvertedValue = (unit) => {
    return convert(inputValue, inputUnit, unit);
  };

  const getComparisonValue = (item) => {
    return inputValue / parseScientific(item.value);
  };

  if (!theme) return null; // Or a loading spinner

  return (
    <div className="p-4 rounded-xl" style={{ backgroundColor: theme.surface }}>
      <h2
        className="text-2xl font-bold mb-4"
        style={{ color: theme.primary, fontFamily: theme.font }}
      >
        Unit Converter
      </h2>

      <div className="mb-4">
        <input
          type="number"
          value={inputValue}
          onChange={handleInputChange}
          className="p-2 rounded border w-full"
          style={{
            backgroundColor: theme.base,
            color: theme.text,
            fontFamily: theme.font,
          }}
        />
        <select
          value={inputUnit?.id || ""}
          onChange={(e) => {
            const selected = units.find((u) => u.id === e.target.value);
            setInputUnit(selected);
          }}
          className="mt-2 p-2 rounded border w-full"
          style={{ backgroundColor: theme.base, fontFamily: theme.font }}
        >
          {units.map((unit) => (
            <option key={unit.id} value={unit.id}>
              {unit.name}
            </option>
          ))}
        </select>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
        {outputUnits.map((unit, index) => {
          const result = getConvertedValue(unit);
          return (
            <div
              key={index}
              className="p-4 rounded-lg shadow"
              style={{ backgroundColor: theme.box }}
            >
              <div className="text-lg font-semibold mb-2" style={{ color: theme.primary }}>
                {viewMode === "scientific"
                  ? formatNumberString(result, true)
                  : formatNumber(result)}
              </div>
              <select
                value={unit.id}
                onChange={(e) => {
                  const updated = units.find((u) => u.id === e.target.value);
                  const updatedList = [...outputUnits];
                  updatedList[index] = updated;
                  setOutputUnits(updatedList);
                }}
                className="w-full mt-2 p-2 rounded border"
                style={{ backgroundColor: theme.base }}
              >
                {units.map((u) => (
                  <option key={u.id} value={u.id}>
                    {u.name}
                  </option>
                ))}
              </select>
            </div>
          );
        })}
      </div>

      <div className="flex items-center justify-between mb-4">
        <h3 className="text-xl font-bold" style={{ color: theme.primary, fontFamily: theme.font }}>
          Real-World Comparison
        </h3>
        <div>
          <button
            onClick={() => setViewMode("general")}
            className={`px-3 py-1 rounded-l ${
              viewMode === "general" ? "font-bold" : ""
            }`}
            style={{
              backgroundColor: viewMode === "general" ? theme.primary : theme.box,
              color: viewMode === "general" ? "#fff" : "#000",
            }}
          >
            General
          </button>
          <button
            onClick={() => setViewMode("scientific")}
            className={`px-3 py-1 rounded-r ${
              viewMode === "scientific" ? "font-bold" : ""
            }`}
            style={{
              backgroundColor: viewMode === "scientific" ? theme.primary : theme.box,
              color: viewMode === "scientific" ? "#fff" : "#000",
            }}
          >
            Scientific
          </button>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {comparisonItems.map((item, index) => (
          <RealWorldBox
            key={index}
            item={item}
            value={getComparisonValue(item)}
            theme={theme}
            viewMode={viewMode}
          />
        ))}
      </div>
    </div>
  );
}

export default Converter;
