import React, { createContext, useEffect, useState, useContext } from "react";
import pb from "../utils/pocketbaseClient";

const ThemeContext = createContext();

export const ThemeProvider = ({ children }) => {
  const [theme, setTheme] = useState(null);

  useEffect(() => {
    const fetchTheme = async () => {
      try {
        // Fetch the singleton theme settings
        const record = await pb.collection("site_settings").getFirstListItem('');

        // Set theme object
        setTheme({
          primary_color: record.primary_color,       // #feb73f
          surface_color: record.surface_color,       // #f3f4f6
          base_color: record.base_color,             // #ffffff
          font_family: record.font_family_main,      // 'Poppins', sans-serif
          logo_url: pb.files.getUrl(record, record.logo_url),
          favicon_url: pb.files.getUrl(record, record.favicon_url),
        });

        // Apply favicon dynamically
        if (record.favicon_url) {
          const favicon = document.querySelector("link[rel~='icon']") || document.createElement("link");
          favicon.rel = "icon";
          favicon.href = pb.files.getUrl(record, record.favicon_url);
          document.head.appendChild(favicon);
        }

        // Apply font-family globally
        if (record.font_family_main) {
          document.body.style.fontFamily = record.font_family_main;
        }

      } catch (err) {
        console.error("Failed to load theme settings:", err);
      }
    };

    fetchTheme();
  }, []);

  return (
    <ThemeContext.Provider value={theme}>
      {children}
    </ThemeContext.Provider>
  );
};

export const useTheme = () => useContext(ThemeContext);
