import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { formatNumber } from "../utils/formatNumber";
import { useTheme } from "../context/ThemeContext";
import MoleConverter from "./MoleConverter";

function parseScientific(val) {
  if (val == null) return null;
  if (typeof val === "number") return val;
  if (typeof val === "object" && val.hasOwnProperty("value")) return val.value;
  return parseFloat(val);
}

export default function Converter({ categoryId, categoryName }) {
  const isMole = categoryName?.toLowerCase() === "mole";

  // ⛔️ If Mole, render dedicated component
  if (isMole) {
    return <MoleConverter categoryId={categoryId} />;
  }

  // ✅ Default converter logic for all other categories
  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [inputValue, setInputValue] = useState(1);
  const [selectedUnit, setSelectedUnit] = useState(null);
  const [toggleScientific, setToggleScientific] = useState([false, false, false]);
  const theme = useTheme();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const unitRes = await pb.collection("units").getFullList({ filter: `category="${categoryId}"` });
        const itemRes = await pb.collection("real_world_items").getFullList({ filter: `category="${categoryId}"` });

        setUnits(unitRes);
        setSelectedUnit(unitRes[0]);
        setRealWorldItems(itemRes);
      } catch (err) {
        console.error("Data fetch error:", err);
      }
    };

    fetchData();
  }, [categoryId]);

  const convert = (unit) => {
    const base = parseScientific(selectedUnit?.value);
    const target = parseScientific(unit.value);
    return (inputValue * base) / target;
  };

  const comparisonRatio = (item) => {
    const itemVal = parseScientific(item.value);
    return inputValue / itemVal;
  };

  const toggleFormat = (index) => {
    const updated = [...toggleScientific];
    updated[index] = !updated[index];
    setToggleScientific(updated);
  };

  return (
    <div className="grid gap-6">
      {/* Input Section */}
      <div className="flex gap-3 items-center">
        <input
          type="number"
          className="border rounded px-3 py-2"
          value={inputValue}
          onChange={(e) => setInputValue(Number(e.target.value))}
        />
        <select
          value={selectedUnit?.id}
          onChange={(e) => setSelectedUnit(units.find((u) => u.id === e.target.value))}
          className="border rounded px-3 py-2"
        >
          {units.map((unit) => (
            <option key={unit.id} value={unit.id}>
              {unit.name}
            </option>
          ))}
        </select>
      </div>

      {/* 3 Conversion Boxes */}
      <div className="grid md:grid-cols-3 gap-4">
        {[0, 1, 2].map((i) => {
          const unit = units[i % units.length];
          const value = convert(unit);
          const display = toggleScientific[i]
            ? value.toExponential(3)
            : formatNumber(value);

          return (
            <div
              key={unit.id}
              className="rounded-xl shadow p-4 flex flex-col items-center"
              style={{ backgroundColor: theme?.box || "#fff" }}
            >
              <div className="font-bold text-xl">{display}</div>
              <div className="text-sm mb-2">{unit.name}</div>
              <button
                className="text-xs text-blue-500 underline"
                onClick={() => toggleFormat(i)}
              >
                {toggleScientific[i] ? "General" : "Scientific"}
              </button>
            </div>
          );
        })}
      </div>

      {/* 3 Real World Boxes */}
      <div className="grid md:grid-cols-3 gap-4 mt-4">
        {[0, 1, 2].map((i) => {
          const item = realWorldItems[i % realWorldItems.length];
          const ratio = comparisonRatio(item);
          const display = toggleScientific[i]
            ? ratio.toExponential(3)
            : formatNumber(ratio);

          return (
            <div
              key={item.id}
              className="rounded-xl shadow p-4 flex flex-col items-center"
              style={{ backgroundColor: theme?.box || "#fff" }}
            >
              <div className="font-bold text-xl">{display}</div>
              <div className="text-sm text-center">{item.name}</div>
              <div className="text-xs text-gray-500 text-center">{item.note}</div>
              <button
                className="text-xs text-blue-500 underline mt-1"
                onClick={() => toggleFormat(i)}
              >
                {toggleScientific[i] ? "General" : "Scientific"}
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );
}
