import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import pb from "../utils/pocketbaseClient";
import AuxiliaryConverter from "./AuxiliaryConverter";
import Converter from "./Converter";
import FunFacts from "./FunFacts";

function CategoryPage() {
  const { slug } = useParams();  // get slug from URL param
  const [category, setCategory] = useState(null);
  const [auxiliaryCategories, setAuxiliaryCategories] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchCategoryBySlug = async () => {
      try {
        // Fetch category by slug and expand auxiliary relation
        const mainCategoryList = await pb.collection("categories").getFullList(1, {
          filter: `slug = "${slug}"`,
          expand: "auxiliary",
        });

        if (mainCategoryList.length === 0) {
          setCategory(null);
          setLoading(false);
          return;
        }

        const mainCategory = mainCategoryList[0];
        setCategory(mainCategory);

        // Set auxiliary categories (expanded)
        const auxiliary = mainCategory.expand?.auxiliary || [];
        setAuxiliaryCategories(auxiliary);

        setLoading(false);
      } catch (err) {
        console.error("Error fetching category by slug:", err);
        setLoading(false);
      }
    };

    if (slug) {
      fetchCategoryBySlug();
    }
  }, [slug]);

  if (loading) return <div className="text-center py-10">Loading category...</div>;
  if (!category) return <div className="text-center py-10">Category not found.</div>;

  return (
    <div className="space-y-12 px-4 sm:px-6 lg:px-8">
      {/* Auxiliary Section */}
      {auxiliaryCategories.length > 0 && (
        <div>
          <h2 className="text-2xl font-bold mb-4 text-center text-gray-700">Auxiliary Calculation</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
            {auxiliaryCategories.map((cat) => (
              <div key={cat.id} className="bg-white p-4 rounded shadow">
                <h3 className="text-xl font-bold text-center mb-4 text-gray-700">{cat.name}</h3>
                <div className="flex flex-col md:flex-row gap-4 justify-between items-start">
                  <AuxiliaryConverter categoryId={cat.id} />
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Main Converter */}
      <div>
        <h2 className="text-2xl font-bold mb-4 text-center text-gray-700">Main Calculation</h2>
        <div className="w-full">
          <Converter categoryId={category.id} />
        </div>
      </div>

      {/* Fun Facts */}
      <FunFacts categoryId={category.id} />
    </div>
  );
}

export default CategoryPage;
