import React, { useState } from 'react';
import VolumeComparisonFinal from './components/VolumeComparisonFinal';


const categories = [
  "Acceleration", "Angle", "Angular Acceleration", "Angular Velocity",
  "Area", "Capacitance", "Charge", "Current", "Density", "Electric Field",
  "Energy", "Force", "Frequency", "Illuminance", "Inertia", "Length",
  "Mass", "Resistance", "Resistivity", "Specific Heat", "Speed",
  "Surface Tension", "Thermal Conductivity", "Time", "Torque",
  "Viscosity", "Volume", "Voltage"
];

export default function App() {
  const [activeCategory, setActiveCategory] = useState(null);


const renderConverter = () => {
  switch (activeCategory) {
    case "Volume":
      return <VolumeComparisonFinal />;




      default:
        return (
          <div className="text-center py-12 text-gray-600 text-lg">
            {activeCategory} converter is coming soon...
          </div>
        );
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-100">
      {/* Header */}
      <header className="bg-white shadow py-4 px-6">
        <div className="text-2xl font-bold text-blue-600">üîÑ UnitCompare</div>
      </header>

      {/* Main Content */}
      <main className="flex-grow p-6">
        {!activeCategory ? (
          <>
            <h2 className="text-xl font-semibold text-center mb-6">
              Select a Conversion Category
            </h2>
            <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4">
              {categories.map((cat) => (
                <button
                  key={cat}
                  onClick={() => setActiveCategory(cat)}
                  className="bg-white shadow hover:bg-blue-50 text-gray-800 font-medium border border-gray-200 rounded px-4 py-3 text-center"
                >
                  {cat}
                </button>
              ))}
            </div>
          </>
        ) : (
          <>
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-semibold">{activeCategory} Converter</h2>
              <button
                onClick={() => setActiveCategory(null)}
                className="text-sm text-blue-600 hover:underline"
              >
                ‚Üê Back to categories
              </button>
            </div>
            {renderConverter()}
          </>
        )}
      </main>

      {/* Footer */}
      <footer className="bg-white border-t text-center text-sm text-gray-500 py-4 mt-6">
        ¬© 2025 UnitCompare. All rights reserved.
      </footer>
    </div>
  );
}
