// CategoryPage.jsx
import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import AuxiliaryConverter from "./AuxiliaryConverter";
import Converter from "./Converter";
import FunFacts from "./FunFacts";

function CategoryPage({ categoryName }) {
  const [categoryId, setCategoryId] = useState(null);
  const [auxiliaryCategories, setAuxiliaryCategories] = useState([]);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const allCategories = await pb.collection("categories").getFullList();
        const mainCategory = allCategories.find((c) => c.name === categoryName);
        if (mainCategory) {
          setCategoryId(mainCategory.id);

          const auxNames = ["Time", "Speed"];
          const auxCats = allCategories.filter((c) => auxNames.includes(c.name));
          setAuxiliaryCategories(auxCats);
        }
      } catch (err) {
        console.error("Error fetching categories:", err);
      }
    };

    if (categoryName) fetchCategories();
  }, [categoryName]);

  if (!categoryId) return <div className="text-center py-10">Loading category...</div>;

  return (
    <div className="space-y-12 px-4 sm:px-6 lg:px-8">
      {/* Auxiliary Section */}
      <div>
        <h2 className="text-2xl font-bold mb-4 text-center text-gray-700">Auxiliary Calculation</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
          {auxiliaryCategories.map((cat) => (
            <div key={cat.id} className="bg-white p-4 rounded shadow">
              <h2 className="text-xl font-bold text-center mb-4 text-gray-700">{cat.name}</h2>
              <div className="flex flex-col md:flex-row gap-4 justify-between items-start">
                <AuxiliaryConverter categoryId={cat.id} />
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Main Converter */}
      <div>
        <h2 className="text-2xl font-bold mb-4 text-center text-gray-700">Main Calculation</h2>
        <div className="w-full">
          <Converter categoryId={categoryId} />
        </div>
      </div>

      {/* Fun Facts */}
      <FunFacts categoryId={categoryId} />
    </div>
  );
}

export default CategoryPage;
